//https://iquilezles.org/articles/palettes/
//[[0.439 0.034 0.411] [0.742 0.658 0.198] [1.271 1.503 0.636] [1.615 2.178 1.561]]
vec3 palette( float t ) {
    vec3 a = vec3(.439, .034, .411);
    vec3 b = vec3(.742, .658, .198);
    vec3 c = vec3(1.271, 1.503, .636);
    vec3 d = vec3(1.615, 2.178, 1.561);

    return a + b*cos( 6.28318*(c*t+d) );
}

//https://iquilezles.org/articles/distfunctions2d/
float sdRoundedX( in vec2 p, in float w, in float r )
{
    p = abs(p);
    return length(p-min(p.x+p.y,w)*0.5) - r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.5; i < 4.5; i++) {
        uv = fract(uv * 1.5) - 0.5;

        float d = sdRoundedX(uv, abs(sin(iTime*0.2+i)), i*10.);

        vec3 col = palette(sin(pow(length(uv0), 1.)) + iTime*0.4 + i*1.);

        d = sin(d*2. + i);
        d = abs(d);

        d = pow(.01 / pow(d, .5), .4);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
