// CLICK MOUSE AND MOVE
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
void mainImage( out vec4 c, in vec2 xy )
{
    c=vec4(0,0,0,1);
    vec2 O=xy.xy;
    vec2 M=iMouse.xy;
    if(M.x+M.y<1.0)M=iResolution.xy/2.0;
    float sin60=sin(radians(60.0));
    float cos60=cos(radians(60.0));

    //fix coords
    xy.x=mod(xy.x,(256.0-64.0)/sin60);
    vec2 o=abs(xy);    
    xy.y+=32.0;
    xy.x-=128.0*sin60;
    float x=abs(xy.x);
    float y=abs(xy.y);
    xy=abs(xy);    
    xy.x-=32.0/sin60;
    xy.y=32.0-mod(xy.y,64.0);
    if(mod(o.x,512.0*sin60)>=256.0*sin60){xy.x=32.0*5.0/8.0-xy.x;};
    if(mod(y,128.0)>=64.0)xy.x-=32.0/sin60;
    xy*=rot(ceil(M.x/8.0));//rotated by mouse
    vec2 XY=vec2(xy.y*cos60-xy.x*sin60,xy.y*cos60+xy.x*sin60);//oblique lines

    //yellow bordes backlight (by mouse)
    if(length(ceil(vec2(M.x,(M.y)))-O)<=224.0)c=vec4(1,1,0,1);
    //red bordes backlight (by mouse)
    if(length(ceil(vec2(M.x/111.0-64.0/111.0,(M.y)/64.0-30.0/64.0))*vec2(111.0,64.0)-O)<=111.0)c=vec4(1,0,0,1);

    //draw all octagons
    if(abs(xy.y)+abs(XY.x)+abs(XY.y)<=56.0){c=vec4(0.4,0.8,1,1);}
    if(abs(xy.y)+abs(XY.x)+abs(XY.y)>=64.0){c=vec4(0,0,0,1);}
    if(abs(xy.y)+abs(XY.x)+abs(XY.y)<=50.0){
        c=vec4(1.8,1.8,1.8,1);
        c*=11.0/sqrt(abs(xy.x*xy.x)+abs(xy.y*xy.y));//shadows
    }
}
