{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 6; k++){
        //if(uv.y>0.) uv.x += .5;
        //else uv.y += .5;

        uv =
            abs(.5+uv-t2)/1.5
        ;
        //uv += sign(uv-uv.yx)/2.+.5;
        if(uv.y>uv.x) uv = uv.yx;
        //else k += 1;

        t2 =
            -triangle_wave(uv-.5)
            //-triangle_wave(uv-.5+floor(.5+t2.y+t2.x))
        ;
        //if(t2.y>t2.x) t2 = t2.yx;
        uv =
            t2-triangle_wave(uv.yx)
            //t2-triangle_wave(uv.yx+floor(.5+t2.y+t2.x))
        ;

        {t2 = t2.yx*sign(t2.y-t2.x); uv=uv.yx;}


        col =
            vec3(col.zx,max(abs(uv.x-uv.y)*sign(t2.x-t2.y),0.))
        ;
        //uv /= 1.+mod(uv.y,2.);
    }
    fragColor = vec4(col*2.,1.0);
}
