vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)),
			 dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}


float noise( in vec2 p )
{
	const float K1 = 0.366025404; // (sqrt(3)-1)/2;
	const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y)*K1 );

	vec2 a = p - i + (i.x+i.y)*K2;
	vec2 o = (a.x>a.y) ? vec2(1.0,0.0) : vec2(0.0,1.0);
	vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

	vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

	return dot( n, vec3(70.0) );
}


#define OCTAVES 10
#define PI 3.141592653589793
float fbm (in vec2 st) {
    // Initial values
    float value = 0.0;
    float amplitude = .5;
    float frequency = 2.;
    //
    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * (noise(st));
        st *= 2.;
        amplitude *= .5;
    }
    return value;
}

float squared(float a){

    return a*a;
}


vec3 getColor(float c){
    return vec3(1.5*c, c*c*c, c*c*c*c*c*c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (2.*fragCoord.xy - iResolution.xy)/iResolution.y;
    vec2 uv0 = 1.*uv;


    //distorted uv0 to uv1
    float n1 = fbm(uv0);
    float n2 = fbm(uv - vec2(0., 1.*iTime));
    vec2 uv1 = (uv0 + vec2(0.2*n1, 0.22*n1));

    //ring shape

    float radius = 0.7;
    float c = abs(length(uv1) - radius);    
    float m =fbm(uv1);
    float f = 1.- smoothstep(0.0, 0.03, c);


    //fire mod https://www.shadertoy.com/view/XsXSWS 

    uv0= vec2(c); //attach fire mod to ring
    vec2 timing = iTime*vec2(0.0,-1.);
    float n = fbm(uv1+timing);
    float e = 1. - 16. * pow( max( 0., length(uv0*vec2(2.8+uv0.y*1.5,.75) ) - n*max( 0., uv0.y+0.25 ) ),1.7);
    float e1 = n * e * (1.5-pow(2.50*uv0.y,2.));
	e1=1. - clamp(e1,0.,1.);


    //Attach color
    vec3 col = vec3(0.);
    vec3 ring = getColor(f);
    vec3 flame =mix(vec3(0.), getColor(0.7*e1), e);


    col = (ring + flame);


    // Output to screen
    fragColor = vec4(col,1.0);
}
