vec3 pallet(in float t)
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.698, 0.028, 0.968);
    return a + b*cos( 6.28318*(c*t+d) );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    vec3 finalColor = vec3(0.0);

    for(float i = 0.; i < 3.; i++)
    {

        // Divide the original UV into smaller fracts of itself
        uv = fract(uv * 1.5) - .5;

        // the distance from the cender of the uv (SDF)
        float d = length(uv) * exp(-length(uv0));

        // the color, based on the original uv
        vec3 col = pallet(length(uv0) + i*.452 + iTime);

        // move the SDFs
        d = sin(d*8. + iTime)/8.;
        d = abs(d);

        // the range we want to see of the SDFs, this also causes a glow
        d = pow(0.008/d,1.3);


        // multiply our color pallet by the visible portion of the SDF
        col *= d;

        // Iterate
        finalColor += col;
    }



    fragColor = vec4(finalColor,1.0);

}
