const float PI = 3.14159265359f;


vec4 eveningLight(vec2 uv){
    float mode = -10.5f + 0.5*sin(iTime*0.5f)-0.2f;
    vec3 col = 0.5 + 0.5*cos(mode+uv.xyx+vec3(4,2,1));

    return vec4(col,1.0);

}

float sin01(float x){
    return 0.5f*sin(x)+0.5f;
}
float cos01(float x){
    return 0.5f+cos(x)+0.5;
}

vec4 weekend(vec2 uv){
    //vec4 col1 = vec4(36/255, 255/255, 65/255, 1);
    vec4 col1 = vec4(128.0f/255.0f, 240.0f/255.0f, 175.0f/255.0f, 1);
    vec4 col2 = vec4(0.8, 0.92f, 0.92f, 1);

    float timeScaled = iTime * 0.25f;

    //float mixer = sin01(timeScaled + (uv.y*90.f)) * uv.x;
    //mixer += sin01(PI + timeScaled + uv.x*75.5f) * uv.y;

    float mixer = sin01(timeScaled + PI)*uv.x;
    mixer += sin01(timeScaled + PI)*uv.y;

    return mix(col1, col2, mixer);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = eveningLight(uv);
    //fragColor = weekend(uv);
}
