//
//  Trying to make a roadless random city for a roguelike game maybe, using the "simple
//  tiled model" idea from Wave Function Collapse:
//
//      https://github.com/mxgmn/WaveFunctionCollapse
//
//  The entire buffer is simulated, so you're only looking at the center 1/64th of the
//  buffer when the shader boots, chosen so that the 8x8 tile pixels are 1:1 with screen
//  pixels. Click and drag the mouse up/left to zoom out.
//
//  The tiles are defined in the Common tab, and are designed to be easy to change. Forks
//  of this shader are welcome if you draw your own tiles and make something intereting
//  and new out of it. But please post a comment here so I don't miss it!
//
//  The rendering was probably harder to get working than the simulation, because I
//  wanted to be able to zoom out and watch the simulation from farther away. My first
//  attempt had horrible aliasing artifacts so I had to add this box clipping system.
//
//  I'm curious if the mouse camera controls are effective or not for other people. Once
//  you get used to it, it's handy for zooming in on specific details, I think.
//
//  Buffer A computes the wave function collapse
//  Buffer B handles camera controls
//
// ---------------------------------------------------------------------------------------

// compute the overlapping area between two axis aligned boxes
float boxClip(vec4 a, vec4 b)
{
    vec4 i = vec4(max(a.xy, b.xy), min(a.zw, b.zw));
    vec2 e = i.zw - i.xy;
    return any(lessThanEqual(e, vec2(0))) ? 0. : e.x * e.y;
}

// rotate a pixel index
ivec2 rotIdx(ivec2 v, int r)
{
    if (r == 0) return ivec2(v.x, 7 - v.y);
    if (r == 1) return v.yx;
    if (r == 2) return ivec2(7 - v.x, v.y);
    if (r == 3) return 7 - v.yx;
}

#define GET_SUBTILE(T, U) ((TILES[T].image[U.y] >> ((7 - U.x) * 3)) % 8u)

// compute the color contribution of a single tile, clipped to the screen pixel
vec3 tileClip(int type, int rot, vec4 clipBox)
{
    vec3 sum = vec3(0);
    for (ivec2 v = ivec2(0); v.x < 8; ++v.x)
    for (v.y = 0; v.y < 8; ++v.y)
    {
        uint value = GET_SUBTILE(type, rotIdx(v, rot));
        sum += subtileColor(value, ((v.x + v.y) & 1) == 0) * boxClip(clipBox, vec4(vec2(v) / 8., (vec2(v) + 1.) / 8.));
    }
    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fxState state = fxGetState();

    fragCoord -= .5 * iResolution.xy;
    fragCoord *= state.zoom;
    fragCoord += state.center * iResolution.y;
    fragCoord += .5 * iResolution.xy;

    // compute the tile address and coordinates within the tile for the lower left and upper right corners of the pixel
    vec2 llc = fragCoord - .5 * state.zoom;
    ivec2 llt = ivec2(llc);
    llc = fract(llc);
    vec2 urc = fragCoord + .5 * state.zoom;
    ivec2 urt = ivec2(urc);
    urc = fract(urc);

    vec3 sum = vec3(0);

    // loop over all the tiles, clipping as we go
    for (ivec2 tile = llt; tile.x <= urt.x; ++tile.x)
    for (tile.y = llt.y; tile.y <= urt.y; ++tile.y)
    {
        if (all(greaterThan(tile, ivec2(0))) && all(lessThan(tile, ivec2(iResolution.xy))))
        {
            vec4 v = texelFetch(iChannel0, tile, 0);
            int type = int(v.x);
            int rot = int(v.y);

            vec4 clipBox = vec4(0, 0, 1, 1);

            // clip one or more sides if necessary
            if (tile.x == llt.x) clipBox.x = llc.x;
            if (tile.y == llt.y) clipBox.y = llc.y;
            if (tile.x == urt.x) clipBox.z = urc.x;
            if (tile.y == urt.y) clipBox.w = urc.y;

            sum += tileClip(type, rot, clipBox);
        }
    }

    fragColor = sqrt(vec4(sum / (state.zoom * state.zoom), 1));
}

vec3 subtileColor(uint value, bool parity)
{
    if (value == 0u) return vec3(0);
    if (value == 1u) return vec3(1,0,0);
    if (value == 2u) return vec3(.3,.3,.3);
    if (value == 3u) return vec3(0,0,1);
    if (value == 4u) return vec3(.5,0,0);
    if (value == 5u) return parity ? vec3(.5,.1,.1) : vec3(.3,.1,.1);
    if (value == 6u) return vec3(0,0,.5);
    if (value == 7u) return parity ? vec3(.1,.1,.5) : vec3(.1,.1,.3);
    return vec3(1,0,1);
}

struct Tile
{
    mat3 shape; // defines how this tile matches to neighbors
    float prob; // lower numbers will cause this tile to be chosen less often
    uint image[8]; // 8-color octal array defining how to render the tile, referencing the above colors
};

