// Integer Hash - II by iq
// https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;  // GLIB C

vec3 hash3( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

#define keyClick(ascii)   ( texelFetch(iChannel3,ivec2(ascii,1),0).x > 0.)
#define keyDown(ascii)    ( texelFetch(iChannel3,ivec2(ascii,0),0).x > 0.)

#define KEY_SHIFT 16
#define KEY_SPACE 32

struct fxState
{
    float resolution;
    float zoom;
    float oldZoom;
    vec2 center;
    vec2 oldCenter;
    bool attract;
};

fxState fxGetStateInternal(sampler2D sampler)
{
    vec4 data0 = texelFetch(sampler, ivec2(0), 0);
    vec4 data1 = texelFetch(sampler, ivec2(1, 0), 0);
    fxState state;
    state.resolution = data0.x;
    state.zoom = data0.y;
    state.oldZoom = data0.z;
    state.attract = data0.w != 0.;
    state.center = data1.xy;
    state.oldCenter = data1.zw;
    return state;
}

#define fxGetState() fxGetStateInternal(iChannel1)

vec4 fxPutState(fxState state, ivec2 ifc)
{
    if (ifc == ivec2(0))
    {
        return vec4(state.resolution, state.zoom, state.oldZoom, state.attract ? 1. : 0.);
    }

    return vec4(state.center, state.oldCenter);
}

// ---------------------------------------------------------------------------------------
// The room where it happens.
// ---------------------------------------------------------------------------------------

int g_NumValidTiles;
const int MAX_VALID_TILES = NUM_TILES * 4;
int g_ValidTiles[MAX_VALID_TILES];
int g_ValidTileRs[MAX_VALID_TILES];

#define R1(m) mat3(m[0][2], m[1][2], m[2][2], m[0][1], m[1][1], m[2][1], m[0][0], m[1][0], m[2][0])

// rotate a shape matrix r times to the right
mat3 rotShape(mat3 s, int r)
{
    while (r < 0) r += 4;
    r = r % 4;
    if (r == 1) s = R1(s);
    else if (r == 2) s = R1(R1(s));
    else if (r == 3) s = R1(R1(R1(s)));

    return s;
}

bool tilesMatch(mat3 a, mat3 b) { return a[0][0] == b[2][0] && a[0][1] == b[2][1] && a[0][2] == b[2][2]; }

// find the full array of tiles that could be compatible with a particular tile already placed
void findValidTiles(vec4 n, int r)
{
    mat3 nShape = rotShape(TILES[int(n.x)].shape, int(n.y) + r);

    for (int i = 0; i < NUM_TILES; ++i)
    {
        mat3 myShape = TILES[i].shape;
        for (int d = 0; d < 4; ++d)
        {
            if (tilesMatch(rotShape(myShape, d), nShape))
            {
                g_ValidTiles[g_NumValidTiles] = i;
                g_ValidTileRs[g_NumValidTiles] = (d + 4 - r) % 4;
                g_NumValidTiles++;
            }
        }
    }
}

// remove any tiles from the array that do not match the particular tile already placed
void removeInvalidTiles(vec4 n, int r)
{
    mat3 nShape = rotShape(TILES[int(n.x)].shape, int(n.y) + r);

    for (int i = 0; i < g_NumValidTiles; ++i)
    {
        mat3 myShape = rotShape(TILES[g_ValidTiles[i]].shape, g_ValidTileRs[i] + r);
        if (!tilesMatch(myShape, nShape))
        {
            g_ValidTiles[i] = g_ValidTiles[g_NumValidTiles - 1];
            g_ValidTileRs[i] = g_ValidTileRs[g_NumValidTiles - 1];
            g_NumValidTiles--;
            i--;
        }        
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fxState state = fxGetState();
    ivec2 ifc = ivec2(fragCoord);
    if (iFrame == 0 || state.resolution < 0.)
    {
        // reset
        fragColor = vec4(0);

        return;
    }

    fragColor = texelFetch(iChannel0, ifc, 0);

    // update in an alternating checkerboard pattern, so no tile is updating on the same tick as any neighbor
    if (((ifc.x + ifc.y) & 1) == (iFrame & 1)) return;

    vec4 ns[4] = vec4[]( texelFetch(iChannel0, ifc + ivec2(0, 1), 0),
                         texelFetch(iChannel0, ifc + ivec2(1, 0), 0),
                         texelFetch(iChannel0, ifc + ivec2(0, -1), 0),
                         texelFetch(iChannel0, ifc + ivec2(-1, 0), 0) );

    // reset when requested by neighbor that can't find a solution
    if (ns[0].z > 0. || ns[1].z > 0. || ns[2].z > 0. || ns[3].z > 0. || fragColor.z > 0.)
    {
        fragColor.x = 0.;
        fragColor.y = 0.;
        fragColor.z = max(0., max(max(max(max(fragColor.z, ns[0].z), ns[1].z), ns[2].z), ns[3].z) - 1.);
        return;
    }

    // empty cells with no neighbors have a chance to spontaneously collapse
    if (fragColor.x == 0. && ns[0].x == 0. && ns[1].x == 0. && ns[2].x == 0. && ns[3].x == 0.)
    {
        vec3 h = hash3(uvec3(fragCoord, iFrame));
        if (h.x < 1e-5)
        {
            fragColor.x = floor(h.y * float(NUM_TILES));
            fragColor.y = floor(h.y * 4.);
            fragColor.z = 0.;
        }
        return;
    }

    if (fragColor.x == 0.)
    {
        // we're empty but one of our neighbors is not, look for a value for ourselves
        g_NumValidTiles = 0;
        bool noSolution = false;
        for (int d = 0; d < 4; ++d)
        {
            if (ns[d].x != 0.)
            {
                if (g_NumValidTiles == 0)
                {
                    // whichever direction is first non-empty will find all its valid tiles
                    findValidTiles(ns[d], d);
                }
                else
                {
                    // every other direction then gets a chance to remove invalid options
                    removeInvalidTiles(ns[d], d);
                    if (noSolution = g_NumValidTiles == 0) break;
                }
            }
        }

        if (noSolution)
        {
            // if we're stuck, ask our neighbors to revert
            fragColor.z = fragColor.z + 1.01;
        }
        else if (g_NumValidTiles > 0)
        {
            vec3 h = hash3(uvec3(fragCoord, iFrame));
            int choice = int(h.y * float(g_NumValidTiles));

            // last chance rejection by probability, just try again next frame
            if (h.z < TILES[g_ValidTiles[choice]].prob)
            {
                // install the chosen valid tile
                fragColor.x = float(g_ValidTiles[choice]);
                fragColor.y = float(g_ValidTileRs[choice]);
            }
        }
    }
}

// ---------------------------------------------------------------------------------------
// Just for handling persistent state: camera position and zoom level.
// ---------------------------------------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 ifc = ivec2(fragCoord);
    if (ifc != ivec2(0) && ifc != ivec2(1, 0)) { discard; }

    fxState state = fxGetState();
    if (iFrame == 0 || keyClick(KEY_SPACE) || iResolution.x * iResolution.y != abs(state.resolution))
    {
        state.resolution = -iResolution.x * iResolution.y;
        state.zoom = 1./8.;
        state.oldZoom = 1./8.;
        state.center = vec2(0);
        state.oldCenter = vec2(0);
        state.attract = true;
    }
    else
    {
        state.resolution = abs(state.resolution);

        if (iMouse.z > 0. && iMouse.w < 0.)
        {
            state.attract = false;
            if (keyDown(KEY_SHIFT))
            {
                vec2 delta = state.zoom * (iMouse.zw * vec2(1, -1) - iMouse.xy) / iResolution.y;
                state.center = state.oldCenter + delta;
            }
            else
            {
                float mouseZoom = (iMouse.y + iMouse.w + iMouse.z - iMouse.x) / iResolution.y;
                state.zoom = state.oldZoom * pow(2., 4. * mouseZoom);
                if (mouseZoom < 0.)
                {
                    vec2 mouseCenter = (vec2(iMouse.z, -iMouse.w) - .5 * iResolution.xy) / iResolution.y;
                    mouseCenter *= state.oldZoom;
                    mouseCenter += state.oldCenter;
                    state.center = mix(state.oldCenter, mouseCenter, min(1.,-5.*mouseZoom));
                }
            }
        }
        else
        {
            state.oldZoom = state.zoom;
            state.oldCenter = state.center;
        }

        if (state.attract)
        {
            state.center = .42*vec2(sin(iTime*.02), cos(iTime*.02));
        }
    }

    fragColor = fxPutState(state, ifc);
}
