void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 rd = 1./iResolution.xy;
    vec2 u = vR*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m =    (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    float d = 0.; 
    for(float i=.5; i<bM; ++i){
    for(float j=.5; j<bT; ++j){
        vec4 t = texture(iChannel0, vec2(i,j*6.)*rd);
        vec2 a = t.xy-u;
        d += float(dot(a,a)<.0003*vR*t.w*bZ);
    }}
    fragColor = vec4(d); 
}

void mainImage( out vec4 fragColor, in vec2 u )
{
    vec2  u2 = vec2(0,u.y!=.5);
    vec4  t = texture(iChannel0, (u-u2)/iResolution.xy);
    vec2  a = vectorField2D(t.xy);
    vec2  b = t.xy-a;
    float z = t.w;
    if(dot(b,b)<.000001||dot(a,a)>vR*vR){z-= iTimeDelta*2.;}
    else                                {z+= iTimeDelta*2.;}
                                         z = min(z,1.);
    if(iFrame==0 || z<0.)
    {
         a = fract(cos(dot(u,vec2(1.111,9.999))+iTime+7.777)
                   *vec2(4567.1234, 8765.2345));
         a = (a*2.-1.)*vR;
         z = .0;
    }
                fragColor = vec4(a,0,z);
    if(u.y!=.5){fragColor = t;}
}
