void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = vec4(0.);

    vec4 o_col = vec4(0.);
    vec2 o_fc = (fragCoord.xy -iResolution.xy*.5) / iResolution.y;
    float n_its = 33.;
    float n_tau = 6.2831;
    float n_dmax = 0.;
    o_fc*=1.2;
    for(float n_it = 0.; n_it<n_its; n_it+=1.){
        // float n_waves = float(int((sin(iTime)*.5+.5)*8.+2.));
        float n_waves = 2.;
        float n_it_nor = n_it/n_its;
        float n_freq = iTime+n_it_nor*n_tau*n_waves;
        float n_radius = .2+.25*(sin(n_freq)*.5+.5);
        vec2 o_p = vec2(
            sin(n_tau * n_it_nor) * n_radius,
            cos(n_tau * n_it_nor) * n_radius
        );
        vec2 o_pdelta = o_fc-o_p;
        float n_dist = length(o_pdelta);
        float n_ang = atan(o_pdelta.y, o_pdelta.x);
        float n_ang_nor = n_ang / n_tau;
        // n_dist = clamp(n_dist, 0., 1.);
        // float n_radius_circl = (sin(iTime+n_it_nor)*.5+.5)+.5;
        // float n_radius_circl = (sin(iTime+n_it_nor*20.)*.5+.5)*.5+.2;
        // float n_radius_circl = (sin(iTime)*.5+.5)*0.3;
        float n_radius_circl = (sin(n_freq)*.5+.5)*.1;
        float n_dist_inout = abs(n_dist*(1./n_radius_circl)-1.);
        n_dist_inout = pow(n_dist_inout, 1./5.);
        n_dist_inout = 1.-n_dist_inout;
        n_dist_inout *=2.;
        float n_int = 1.+sin((n_ang_nor+n_it_nor*3.)*n_tau*2.+iTime);
        n_dist_inout *= .5 + (sin(iTime)*.5+.5)*n_int;

        // float n_d = abs(n_dist-.5);
        // n_dist_inout = smoothstep(0., 1.,n_d );
        // n_dist_inout = pow(n_dist_inout, 2.);
        // float n_d_white = 1.-n_dist_inout;
        // n_d_white = pow(n_d_white, 10.);
        n_dmax = max(n_dmax, n_dist_inout);
        o_col += n_dist_inout;

    }

    fragColor = vec4(n_dmax);
    fragColor = o_col*n_dmax;
    fragColor = vec4(n_dmax);//+(o_col*.02);
}
