#define Middle roundbox(U-vec2(.5,.5)*R,vec2(.05,.6)*R,0.)

#define L max(roundbox(U-vec2(.5,.5)*R,vec2(.12,.4)*R,0.),-Sep+.025*R.x)<0.

#define W max(min(Wire,Middle),-Sep) < 0.


