// https://www.youtube.com/c/InigoQuilez
// https://iquilezles.org


// Signed distance to a 2D rounded box. Tutorials explaining
// how it works: 
//
// https://www.youtube.com/watch?v=62-pRVZuS5c
// https://www.youtube.com/watch?v=s5NGeUV2EyU

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
// and iquilezles.org/articles/distfunctions2d


// b.x = width
// b.y = height
// r.x = roundness top-right  
// r.y = roundness boottom-right
// r.z = roundness top-left
// r.w = roundness bottom-left
float sdRoundBox( in vec2 p, in vec2 b, in vec4 r ) 
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // vec2 cp = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 cp = vec2(2.0*fragCoord.x-(iMouse.x*2.0), 2.0*fragCoord.y-(iMouse.y*2.0))/iResolution.y;
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

	float rbd = sdRoundBox( p, vec2(1.2, 0.7), vec4(0.06) );
    float cd = sdCircle( cp, 0.02 );

    float boxCircleD = 1.0-abs(sdRoundBox( p - cp, vec2(1.2, 0.7), vec4(0.06)));

    vec3 bead = vec3(1.0, 0.8, 0.8);
    bead *= (pow((1.0-(abs(rbd) * 1.0)), 100.0));
    bead *= pow(max(1.0 - (cd * 0.8), 0.0), 100.0);
    bead *= 0.5;

    vec3 redOutline = vec3(1.0, 0.05, 0.1);
    // rounded box component
    redOutline *= (pow((1.0-(abs(rbd*0.8))), 100.0));
    // circle component
    redOutline *= pow(max(1.0-cd*0.5, 0.0), 10.0);
    // redOutline *= 2.0;
    redOutline *= 0.5;

    vec3 whiteOutline = vec3(1.0, 0.7, 0.8);
    // rounded box component
    // TODO: figure out why this makes a box in the center
    whiteOutline *= (pow((1.0-(abs(rbd*0.9))), 250.0));
    // circle component
    whiteOutline *= pow(max(1.0-(cd * 0.4), 0.0), 6.0);
    whiteOutline *= 0.8;
    // whiteOutline *= 3.0;
    // col = vec3(pow(max(1.0-cd, 0.0), 2.0));

    vec3 haloColor = vec3(0.8, 0.0, 0.1);
    vec3 haloFalloff = vec3(0.0);
    haloFalloff += haloColor * pow(max(1.0-(cd*6.0), 0.0), 4.0) * 0.25;
    haloFalloff += haloColor * pow(max(1.0-(cd*4.0), 0.0), 2.0) * 0.1;
    haloFalloff += haloColor * pow(max(1.0-(cd*0.8), 0.0), 2.0) * 0.2;
    haloFalloff += haloColor * pow(max(1.0-(cd*0.3), 0.0), 2.0) * 0.1;

    vec3 col = vec3(0.0);

    // col = mix(col, vec3(0.2, 0.0, 0.0), 1.0-smoothstep(0.0,0.01,abs(cd)) );
    col += redOutline;
    col += haloFalloff * pow(boxCircleD, 100.0);
    col += whiteOutline * pow(boxCircleD, 20.0);
    // col = mix(col, vec3(1.0, 1.0, 1.0), 1.0-smoothstep(0.0,0.005,abs(rbd)));
    col += bead * pow(boxCircleD, 100.0);

	fragColor = vec4(col,1.0);
}
