/* Trippier system */
/* https://www.shadertoy.com/view/cllSzl */

#define r(a) mat2(cos(a + vec4(0,33,11,0)))

void mainImage(out vec4 O, vec2 u) {
    vec2 r  = iResolution.xy; u += u - r;

    float d=0., m=d, t=0.8*sin((iTime) * 0.3 + 20.3) + 0.8, c;
    vec3 g, p, q;

    for (g*=d; m++<(62. + 60.*sin(iTime * 0.8)); 
        p  = abs(d*normalize(vec3(u/r.y*r(t*.3), 1)))) 
        g += (tan(d*.2+vec3(0,1,2))*.5+1.) / (1.+pow(abs(
        c  = length(vec2(length(p.xy)-1.,mod(p.z+t*4.,14.)-7.))-.05)*40.,1.3)),
        q  = fract(p) - .5,
        d += min(c, length(q.xy*r(t) + 
          vec2(0, tan(q.z*6.28+t/.1)*tan(t+length(p.xy))/(6.*sin(t)))) - .1) - d*d/(1. + 0.01 * iTime);

    O.rgb = (tan(d*4.+t+vec3(0,1,2))+1.) / tan(d*.2) * g + g;   
}
