// regolfing 458 chars https://shadertoy.com/view/4lsSRB
// compact simplified version of https://www.shadertoy.com/view/4lfSDn 

// -5 by fab, -3 by coyote

#define r(t)    mat2(cos( t*T + vec4(0,11,33,0))),              //
#define M(a,b)  1. / ( 1./length(a) + 1./length(b) )            //
#define D(s)    min( A = abs(fract(p)+s.2), min(A.y,A.z) ).x,

void mainImage( out vec4 o, vec2 u ) {

    float T=iTime, l=1.,a,b; 
    vec3 p = vec3(u,0)/iResolution.y-.5, d,q,A; p.x-=.4;
    p.xz *= r(.13)  p.yz *= r(.2)
    d = p; p.z += 5.*T;
    for ( o = 1.2 + o*0. ; o.x>0. && l>.01; o-=.012 ) 
        q = mod(p,8.) - 4., 
        l = M(q.xy,M(q.yz,q.xz)) -.7,
        a = D(-) b = D()
     	l = sqrt(l*l+min(a*a,b*b)),
//     	l = sqrt(l*l+min(D(-)*D(-),D()*D())), // would this work on windows ?
        p -= d*l;                             // ( without , at end of #def D )

}
