// CubeMap variant of https://shadertoy.com/view/dlSyDc
// inspired from https://shadertoy.com/view/3sKGWy

void mainImage( out vec4 O, vec2 U )
{
    float LOD = 9.;                                    // less for local extremas
    if (iMouse.z>0.) LOD = floor(10.*iMouse.y/R.y);

    O = texture(iChannel0, U/R).xxxw;                   // source image + resol
    float l = min(10., floor(LOD+log2(1024./O.w)) ),    // select lod
          L = exp2(l);
    vec2 V = ( floor(U/R*1024./L)*L+L/2.+.5)/1024.;     // emulates texelFetch(LOD)
 // vec2 V =   floor(U/R*1024./L)*L         /1024.;                
    vec4 M = C(V,0,l);                                  // fetch partial sum  
 // vec4 M = C(vec2(.5),0,10);                          // fetch partial sum
 // O += M.xyww/1024.; return;                          // ( for debug )
 // O.g += M.w; return;                                 // ( for debug )
    M /= M.z;                                           // normalize position with weight : should show ~invmin
    M.xy *= R;                                          // â†’ window coordinates.

    O = mix(O, vec4(1,0,0,0), smoothstep( 2.5, 1., length(M.xy-U)-LOD )); // plot the position

    O = pow( O, vec4(1./2.2) );                         // back to sRGB
}

// === source image ================================

void mainImage( out vec4 O, vec2 U )
{
    O.x = pow( length( texture(iChannel0, U/R).rb ) , 4. ); 
 // O.x = pow( length( texture(iChannel0, U/R).rb ) / 1.3, 2.2. );// luminance
    O.w = iChannelResolution[0].y;                                // source resolution
}

#define POW 9.  // the larger the better min/max, but more bigfloats precision issues

#define R  iResolution.xy

// CubeMap utils from  https://www.shadertoy.com/view/3tGBDz
vec3 E;
#define C(U,n,l) ( E =   vec3( 2.*fract(U) -1., 1 )                                             \
                       * vec3( n==0||n==5 ?-1:1 , n!=1 ?-1:1 , n>2 ?-1:1 ),                     \
                   E = (n)%3 != 2 ? E.yzx : E.xzy,                                              \
                   textureLod( iChannel1, vec3( E[(n+1)%3], E[(n)%3], E[(n+2)%3] ) , float(l) ) \
                 )

// === prepare (inv)min/max calculation ========================

void mainCubemap( out vec4 O, vec2 U, vec3 o, vec3 D )
{
    vec3 A = abs(D);
    int n = A.x > A.y ? A.x > A.z ? 0 : 2 : A.y > A.z ? 1 : 2; // faceID
    if (D[n]<0.) n += 3;

    O *= 0.;
    if (n>0) return;                                     // we only use Face 0                                  

    U /= 1024.;                                          // normalized coordinate

 // float w = pow( texture(iChannel0, U).x, -POW ) ;     // this approximates MIN. then apply pow(LODmax, -1/POW)
    float w = pow( texture(iChannel0, U).x,  POW ) ;     // this approximates MAX. then apply pow(LODmax,  1/POW)

    O.xy = U*w;                                          // weighted location
    O.z  = w;                                            // weight
 // O.w =  texture(iChannel0, U).x;                      // ( for debug )
}
