float hash(float n) { return fract(sin(n) * 43758.5453123); }

vec2 hash2(vec2 p) {
    vec2 SCALE = vec2(37.0, 71.0);
    vec2 ROTATE = vec2(121.0, 157.0);
    vec2 OFFSET = vec2(17.0, 43.0);

    float s1 = dot(p, SCALE);
    float s2 = dot(p, ROTATE);

    return fract(vec2(sin(s1), sin(s2)) * 1399763.5453123) * 2.0 - 1.0;
}

vec3 hash3(vec3 p) {
    vec3 SCALE = vec3(127.1, 311.7, 419.2);
    vec3 ROTATE = vec3(269.5, 183.3, 246.1);
    vec3 OFFSET = vec3(113.5, 271.9, 124.6);

    vec3 s = vec3(dot(p, SCALE), dot(p, ROTATE), dot(p, OFFSET));
    return fract(sin(s) * 143758.5453) * 2.0 - 1.0;
}

float voronoi(vec2 x) {
    vec2 p = floor(x);
    vec2 f = fract(x);
    vec3 res = vec3(8.0);
    for (int j = -1; j <= 1; j++) {
        for (int i = -1; i <= 1; i++) {
            vec2 b = vec2(i, j);
            vec2 r = vec2(b) - f + hash2(p + b) * 0.35;
            float d = dot(r, r);
            int index = int(hash(dot(p + b, vec2(37, 41))));
            if (d < res[index]) {
                res[index] = d;
            }
        }
    }
    return sqrt(res.x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    float v = voronoi(uv * 2.0);
    fragColor = vec4(vec3(v), 1.0);
}
