void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    st = st * 2.0 - 1.0;  // Normalize to [-1, 1]
    st.x *= iResolution.x / iResolution.y;  // Adjust for aspect ratio

    vec3 color = vec3(0.0); // initialize to black

    for(int i=0; i<3; i++) {  // 3 mandalas for this example
        vec2 s = st;

        // Convert to polar coordinates
        float r = length(s);
        float theta = atan(s.y, s.x);

        // Opposing rotations for moirÃ© effect
        if(i % 2 == 0) {
            theta += iTime * (0.5 + float(i)*0.5);  // Adjust speed for variety
        } else {
            theta -= iTime * (0.5 + float(i)*0.5); 
        }

        float repetitions = 6.0 + float(i);  // More petals for variety
        theta *= repetitions;

        // Convert back to Cartesian coordinates
        s.x = r * cos(theta);
        s.y = r * sin(theta);

        // Adjust mushroom size
        float scaleFactor = 0.5 + float(i) * 0.2; 
        s /= scaleFactor;

        // Mushroom shape
        float capDist = length(s - vec2(0.0, 0.4));
        float cap = (capDist < 0.5) ? 1.0 : 0.0;
        float stem = 1.0;
        if(abs(s.x) > 0.1 || s.y < -0.5 || s.y > 0.2) stem = 0.0;

        float mushroom = max(stem, cap);
        if(mushroom > 0.5) {
            vec3 currentColor;
            if(i == 0) {
                currentColor = vec3(0.000,0.518,1.000);  // Red
            } else if(i == 1) {
                currentColor = vec3(0.882,1.000,0.000);  // Green
            } else {
                currentColor = vec3(0.408,0.643,0.055);  // Blue
            }
            color += currentColor;
        }
    }

    fragColor = vec4(color, 1.0);
}
