vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

vec3 fractal(vec2 uv){
    vec3 col = vec3(0.);
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    vec3 col1 = col;
    float c1=0.;
    for(int k = 0; k < 12; k++){

        uv.y -= 1./4.;

        uv = (uv+t2)/scale;

        uv = (fract(vec2(uv+vec2(.5,1.5))*scale)-.5)/scale;
        //col.x = max(length(uv-t2-c1)/3.,col.x);
        t2 = triangle_wave(uv+.5);
        uv =
            //t2-triangle_wave(uv.yx)+fract(t2/2.)

            //looks better with more iterations
            t2-triangle_wave(uv.yx)-fract(t2/2.)
        ;
        //t2.x = (t2.x+1.5*sign(t2.y-t2.x));
        //uv.x = (uv.x+1.5*sign(uv.y-uv.x)); 

        float f1 = 8.;
        c1 =
            //max(abs(uv.y-uv.x*sign(uv.x-uv.y))/2.,c1)
            max(abs(uv.y+uv.x)/f1,c1)
            //max(abs(uv.y-uv.x)*2./f1,c1)
        ;
        c1 =
            max(1.-abs(2.*c1*f1-1.),c1/f1)

            //more colorful
            //abs(2.*c1-1.)

            //Spooky!
            //max(abs(2.*c1-2.),c1/4.)/2.
        ;
        col.x =
            //max(1./3.,col.x)
            max(length(uv-t2-c1)/3.,col.x)


            //yellow and green
            //max(length(uv-t2-c1)/3.,1.-col.x)

            //purple and gold
            //2. - max((abs(uv.y-uv.x))/3.,col.x)

            //black and white
            //col.x
        ;
        col =
            abs(col-(1.-(c1*col.x)));
        col1 =
            abs(col1*c1-col-1.).yzx
            //abs(col1*c1-col-sign(t2.y-t2.x)).yzx
            //abs(col1*c1-col*sign(t2.y-t2.x)-1.).yzx
        ;
    }
    return col1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    float t1 = 6.;

    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.xy += iTime/t1/12.;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec3 col1 = fractal(uv);
    fragColor = vec4(col1/2.,1.0);
}

float mod1(float a, float b){
    return
        mod(a,b)
        //mod(a+floor(a/8.)*2.,b)
        //mod(a+mod(a,b+3.),b)
    ;
}

void distort(inout float a, inout float b){
    for(float i = 1.; i < mod(floor(a/b),5.); i++){
        //a /=2.0;
        //a /=1.0+mod(floor(a),2.);

        a += mod1(floor(a/2.),b)*i;
    }
}

float fmod(float a, float b){
    distort(a,b);
    return
        mod(floor(a),b)
    ;
}

#define fract1(a) abs(1.-fract(-a)*fract(-a))
//#define fract1(a) pow(fract(a),1.+log(2.)-log(1.+fract(a)))

vec2 synth_(vec2 a1,vec2 a,vec2 a2,vec2 a3,vec2 nb,vec2 a4){
    return log(a1+a*abs(a2-abs(.5-a3*vec2(fract1(nb.x*.998),fract1(nb.y)))*a4));
}
#define synth(a1,a,a2,a3,nb,a4) synth_(vec2(a1),vec2(a),vec2(a2),vec2(a3),vec2(nb),vec2(a4))

vec2 banjo(vec2 a, vec2 nb){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,4./6.)
  + synth(1./8.,a,1./4.,1.,nb*2.,6./4.));
}

vec2 harp(vec2 a, vec2 nb){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,2.)
  + synth(1./8.,a,1./4.,1.,nb*2.,1./2.));
}

vec2 synth_drum(vec2 a, vec2 nb,float m2){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,4./m2)
  + synth(1./8.,a,1./4.,1.,nb*2.,m2/4.));
}

vec2 oud(vec2 a, vec2 nb){
    return a/16./2.*abs(log(.5+a*abs(abs(.5-vec2(fract1(nb.x*2.*.998),fract1(nb.y*2.)))/2.))
      +.5*log(1./8.+a*abs(1./8.-abs(.5-vec2(fract1(nb.x*.998),fract1(nb.y)))/2.)))/2.
    ;
}

vec2 steel_drum(vec2 a, vec2 nb){
return a/16./4.*abs(synth(1./2.,a,1./8.,1.,nb*4.,1.)
  + synth(1./8.,a,1./4.,1.,nb*2.,1./2.))
;
}

vec2 gamelan(vec2 a, vec2 nb){
return
a/16./4.*abs(synth(1./4.,a,1./8.,1.,nb*2.,1./2.)
  +.5*synth(1./4.,a,1./4.,1.,nb*2.,1./2.)) 
;
}

vec2 sitar1(vec2 a, vec2 nb){
    return a/16./2.*abs(log(1./2.+a*abs(abs(.5-vec2(fract1(nb.x*2.*.998),fract1(nb.y*2.)))/2.))
      -log(1./4.+a*(abs(.5-vec2(fract1(nb.x*.998),fract1(nb.y)))/2.)))/2.
    ;
}

vec2 sitar2(vec2 a, vec2 nb){
    return a/16./2.*abs(log(1./2.+a*a*abs(abs(.5-vec2(fract1(nb.x*2.*.998),fract1(nb.y*2.)))/2.))
      -log(1./2.+(abs(.5-vec2(fract1(nb.x*.998),fract1(nb.y)))/2.)))/2.
    ;
}

vec2 sitar(vec2 a, vec2 nb){
return a/16./2.*abs(log(1./8.+.5*a*abs(1./8.-abs(.5-vec2(fract1(nb.x*4.*.998),fract1(nb.y*4.)))/2.))
  -log(1./16.+a*abs(1./8.-abs(.5-vec2(fract1(nb.x*2.*.998),fract1(nb.y*2.)))/2./4.)));
}

