float smin( float a, float b, float k )
{
    a = pow( a, k ); b = pow( b, k );
    return pow( (a*b)/(a+b), 1.0/k );
}

//  2 out, 2 in...
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float voronoi2d(vec2 uv){
    float n = 999.0;
    for (int x = -1; x < 2; x++){
        for (int y = -1; y < 2; y++){
            n = smin(n,distance(floor(uv) + vec2(x,y) + hash22(floor(uv) + vec2(x,y)),uv),8.0);
        }
    }
    return n;
}

vec4 renderMoon(vec2 uv, float spin, float stage){
    float l = uv.x*uv.x + uv.y*uv.y;
    if (l < 1.0){
        uv /= sqrt(1.0 - l) + 1.0;
        vec2 p = vec2(uv.x - spin, uv.y);
        float v = abs(voronoi2d(p*3.0) - 0.3) + abs(voronoi2d(p*7.0)*0.4 - 0.1) +  abs(voronoi2d(p*15.0)*0.1 - 0.05);
        vec3 lightColor = vec3(1);
        vec3 darkColor = vec3(0,0.05,0.1);
        vec3 shadowColor = vec3(0,0.025,0.05);
        vec4 col = vec4(mix(darkColor,lightColor,1.0 - v),smoothstep(1.0,0.97,l));
        float k = (2.0*asin(mod(stage,2.0) - 1.0)/3.14159)*sqrt((1.0 - uv.y*uv.y));
        float s = sign(mod(stage,4.0) - 2.0);
        if (s*uv.x < s*k){
            col.rgb = mix(col.rgb,shadowColor,smoothstep(0.0,0.1,s*(k - uv.x))*0.75);
        }
        return col;
    }
    return vec4(0,0,0,0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.y - 0.5)*5.0;
    vec4 moon = renderMoon(uv,iTime,iTime);
    vec3 col = mix(vec3(0),moon.rgb,moon.a);

    fragColor = vec4(col,1.0);
}
