vec3 hsv2rgb(vec3 c) {
  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = fragCoord.xy / iResolution.xy - 0.5;
    p.x *= iResolution.x / iResolution.y;

    float time = iTime * 2.1;
    vec3 camPos = vec3(0.0, 0.0, -3.0);
    vec3 camTarget = vec3(0.0, 0.0, 0.0);
    vec3 camDir = normalize(camTarget - camPos);
    vec3 camRight = normalize(cross(vec3(0.0, 1.0, 0.0), camDir));
    vec3 camUp = cross(camDir, camRight);
    vec3 rayDir = normalize(vec3(p, 1.0) * mat3(camRight, camUp, camDir));

    vec3 col = vec3(0.0);
    float dist = 100.0;

    for (int i = 0; i < 50; i++) {
        vec3 pos = camPos + rayDir * dist;
        float d = length(pos - vec3(sin(pos.z * 0.5 + time), cos(pos.x * 0.5 + time), sin(pos.y * 0.5 + time)));

        col += hsv2rgb(vec3(d * 0.1 + time * 0.05, 1.0, 1.0));
        dist += 0.05;
    }

    fragColor = vec4(col / 50.0, 1.0);
    fragColor.rgb = pow(fragColor.rgb, vec3(0.45));
    fragColor.r = mix(fragColor.r, fract(sin(dot(fragColor.rgb, vec3(12.9898, 78.233, 98.422)) * 43758.5453)), 0.1);
    fragColor.g = mix(fragColor.g, fract(sin(dot(fragColor.rgb, vec3(12.9898, 78.233, 98.422)) * 43758.5453)), 0.1);
    fragColor.b = mix(fragColor.b, fract(sin(dot(fragColor.rgb, vec3(12.9898, 78.233, 98.422)) * 43758.5453)), 0.1);
}
