float mod1(float a, float b){
    return
        mod(a,b)
        //mod(a+mod(a,b+3.),b)
    ;
}

void distort(inout float a, inout float b){
    for(float i = 1.; i < mod(floor(a/b),3.); i++){
        //a /=2.0;
        //a /=1.0+mod(floor(a),2.);
        a += mod1(floor(a/2.),b)*i;
    }
}

float fmod(float a, float b){
    //distort(a,b);

    //the modulus should be a prime number
    //this makes some interesting rhythms
    float modulus =
        //3.
        5.
        //7.
    ;
    for(float i = 1.; i < mod(floor(a/b),modulus); i++){
        a += mod(floor(a/2.),b)*i;
    }
    float a1 = a;
    return
        mod(floor(a),b)
    ;
}

#define fract1(a) pow(fract(a),1.+log(2.)-log(1.+fract(a)))

vec2 synth(float a1,vec2 a,float a2,float a3,vec2 nb,float a4){
    return log(a1+a*abs(a2-a3*abs(.5-vec2(fract1(nb.x*.998),fract1(nb.y)))*a4));
}

vec2 banjo(vec2 a, vec2 nb){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,4./6.)
  + synth(1./8.,a,1./4.,1.,nb*2.,6./4.));
}

vec2 harp(vec2 a, vec2 nb){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,2.)
  + synth(1./8.,a,1./4.,1.,nb*2.,1./2.));
}

vec2 synth_drum(vec2 a, vec2 nb,float m2){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,4./m2)
  + synth(1./8.,a,1./4.,1.,nb*2.,m2/4.));
}

vec2 oud(vec2 a, vec2 nb){
    return a/16./2.*abs(log(.5+a*abs(abs(.5-vec2(fract1(nb.x*2.*.998),fract1(nb.y*2.)))/2.))
      +.5*log(1./8.+a*abs(1./8.-abs(.5-vec2(fract1(nb.x*.998),fract1(nb.y)))/2.)))/2.
    ;
}

vec2 steel_drum(vec2 a, vec2 nb){
return a/16./4.*abs(synth(1./2.,a,1./8.,1.,nb*4.,1.)
  + synth(1./8.,a,1./4.,1.,nb*2.,1./2.))
;
}

vec2 gamelan(vec2 a, vec2 nb){
return
a/16./4.*abs(log(1./4.+.5*a*abs(1./8.-abs(.5-vec2(fract1(nb.x*4.*.998),fract1(nb.y*4.)))/2.))
  +.5*log(1./4.+a*abs(1./4.-abs(.5-vec2(fract1(nb.x*2.*.998),fract1(nb.y*2.)))/2.))) 
;
}

vec2 sitar5(vec2 a, vec2 nb){
    return a*a/16./4.*abs((abs(1./8.-abs(.5-vec2(fract1(nb.x*4.*.998),fract1(nb.y*4.)))/2.))
      -(a*abs(1./4.-abs(.5-vec2(fract1(nb.x*2.*.998),fract1(nb.y*2.)))/2.))) 
    ;
}

