#define cine ( abs( h.x - h.y ) < 20. ) // for cinshader resolution
#define h iResolution.xy

float f(float a, float b, float c) { return smoothstep(a, b, c); }
float q(float a) {
  float b = a - floor(a);
  b = b > .5 ? 1. : 0.;
  return floor(a) + b;
}
vec2 k(vec2 a) { return vec2(q(a.x), q(a.y)); }

float l(vec2 a) { return dot(a, a); }
mat2 e(float a) { return mat2(cos(a), sin(a), -sin(a), cos(a)); }
float A(float a) { return clamp(a, 0., 1.); }
float r(vec2 a, vec2 b) { return l(a - b); }
float m(vec2 c, vec2 a, vec2 d) {
  vec2 g = c - a, b = d - a;
  return l(g - b * A(dot(g, b) / l(b)));
}
float s(inout vec2 b, float d) {
  float a = 6.28 / d, g = (atan(b.y, b.x) + a * .5) / a, c = floor(g),
        i = a * c;
  b *= e(i);
  return c;
}
vec2 t(inout vec2 a, vec2 b) {
  vec2 c = b * .5, d = floor((a + c) / b);
  a.y -= mod(d.x, 2.) * .05, a = mod(a + c, b) - c;
  return d;
}
vec2 B(inout vec2 a, vec2 b, float c) {
  vec2 d = k(a / -b);
  a = a - b * clamp(k(a / b), -c, c);
  return d;
}
float j(vec2 a, int b) {
  a *= e(.094245);
  vec2 c = t(a, vec2(b == 0 ? .065 : .08));
  return b == 0 ? r(a, vec2(0)) : c.y > 3. ? r(a, vec2(0)) : 1.;
}
float Z( vec2 u ){
    u.x += .1, u.y -= .58, u *= e(.094245);
    return max( 
        abs( u.x ) - 2.1,
        abs( u.y ) - .84
    );
}
float n(vec2 a) {
  float d = .1;
  a.y -= .3, a.x += 2.6;
  for (float b = 0.; b < 10.; b++) {
    a.x -= .8, a.y += cos(b) * .2;
    if( b==2. )
        a.y += .1;
    if( b==3. )
        a.y -= .1;
    vec2 c = a;
    c *= e(.785375), c *= e(b / 20.),
        d = min(d, max(abs(c.x) - .4, abs(c.y) - .5));
  }

  return d;
}
float C(vec2 a) { return max( abs(n(a)), Z( a ) ); }
float D(vec2 a, float b, float E) {
  float d = .3, c = .1;
  a.y += .8, a *= e(cos(iTime) * .1 / b), a.y -= cine ? .25 : .8;

  for (float g = 0.; g < b; g++) {
    a.y -= .17;
    for (float i = 1.; i < E; i++)
      a.x = abs(a.x) - .01 - d * .01, a *= e(-.2), a *= e(cos(iTime) * .1 / b),
      d /= .95;
    c = min(c, m(a, vec2(0, -.2), vec2(0, .1)));
  }
  return c;
}
float F(vec2 a) {
  a.x += .3, a.y -= .7, a *= e(iTime * .1), s(a, 25.), a.x -= .5,
      a *= e(1.57075), B(a, vec2(.2, .6), 1.);
  return abs(max(max(abs(a.x) - .3 * abs(abs(a.y) - .3) + .01, abs(a.y) - .3),
                 mod(a.y + .025, .05) - .025));
}
float G(vec2 a) {
  a.y += .58, a *= e(.094245);
  return abs(max(max(abs(a.x) - 2.1, abs(a.y) - .84),
                 mod(a.y + cos(a.x * 1.2) * .5, .1) - .05));
}
float H(vec2 a) {
  a.x -= 1., a.y += .15;
  return abs(min(min(max(abs(a.x) - .78 * -(a.y - .35), abs(a.y - .35) - .3),
                     max(abs(a.x) - .15, abs(a.y) - .15)),
                 max(abs(a.x - .12) - .02, abs(a.y - .2) - .1)));
}
float I(vec2 a) {
  a.x -= 1., a.y += .13;
  return abs(max(abs(a.x) - .05, abs(a.y) - .05));
}
float J(vec2 a) {
  a.x -= 1., a.y += .15;
  return max(abs(a.x) - .78 * -(a.y - .35), abs(a.y - .35) - .25);
}
float K(vec2 a) {
  a *= e(1.50792);
  return abs(mod(a.x + .028, .047) - .013);
}
vec2 u(vec2 a, float b) {
  a.x += cos(a.y * 90. + b) / 90.;
  return a;
}
float L(vec2 a) {
  a.x -= 1.13, a.y -= .24;
  return abs(min(m(u(a, iTime * 2.), vec2(-.02, -.06), vec2(-.02, .06)),
                 m(u(a, iTime * 2. + 5.5), vec2(.02, -.06), vec2(.02, .06))));
}
float v(vec2 a) {
  a.x += cine ? .5 : .7, a.y += cine ? .8 : .5;
  return max(abs(a.x) - .2 * abs(1. - (a.y / 2. + .5)), abs(a.y) - .4);
}
float M(vec2 a) { return abs(v(a)); }
float o(vec2 b) {
  float a = b.y;
  a = abs(a) - .3, a = clamp(a, -.3, 1.), a = abs(a) - .01;
  return max(abs(b.x) - .35 * a, abs(b.y) - .08);
}
float p(vec2 b, int c) {
  float a = b.y, d = c == 0 ? .35 : .5, g = c == 0 ? .12 : .18;
  a = abs(a) - d, a = clamp(a, -d, 1.), a = abs(a) - .01;
  return max(abs(b.x) - .4 * a, abs(b.y) - g);
}
float N(vec2 a) {
  a.x -= .1, a.y += .28, a *= e(1.099525);
  return p(a, 1);
}
float w(vec2 a) {
  a.x -= .83, a.y += cine ? 1.45 : .98, a *= e(.157075);
  return p(a, 1);
}
float x(vec2 a) {
  vec2 b = a;
  a.x += cine ? .95 : 1.15, a.y += cine ? .95 : .45, a *= e(.785375);
  return min(N(b), p(a, 0));
}
float O(vec2 a) { return abs(x(a)); }
float y(vec2 a) {
  a.x += .2, a.y += .2, a *= e(.785375), a.x -= .3, a.y += .7;
  float c = .05;
  for (float b = 0.; b < 3.; b++)
    a.x = abs(a.x) - c * 1.5, c *= .1, a *= e(b), a.y += cos(b - 3.) * .4,
    a = abs(a) + c * .1;
  return min(o(a), o(a * e(.785375) + vec2(.2, -.1)));
}
float P(vec2 a) { return min(y(a), y(a * e(.785375) + vec2(.2, -.1))); }
float z(vec2 a) {
  vec2 d = a;
  a.x += cine ? 1. : 1.2, a.y += cine ? .9 : .4;
  float c = .2;
  for (float b = 1.; b < 3.; b++)
    a.x = abs(a.x) - c / b * .5, c *= 1.1, a *= e(cos(3. - b));
  return min(o(a), P(d));
}
float Q(vec2 a) { return abs(z(a)); }
float R(vec2 a) {
  a.x += cine ? .5 : .7, a.y += cine ? .4 : .1, a *= e(.471225 - iTime * .5 + .5);
  vec2 c = a, d = a * e(.785375), b = a;
  b = mod(b, .1) - .05, s(a, 4.), a.x -= .4, a *= e(1.57075);
  return min(
      max(max(abs(a.x) - .115 * abs(1. - (a.y / 2. + .5)), abs(a.y) - .3),
          max(abs(b.x) - .03, abs(b.y) - .03)),
      max(max(abs(c.x) - .1, abs(c.y) - .1),
          -max(abs(d.x) - .05, abs(d.y) - .05)));
}
float S(vec2 a) { return abs(R(a)); }


void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)

      float c = min(h.x, h.y);

      vec2 b = cine ? (V * 3. - h.xy * 1.5) / c : (V * 2. - h.xy) / c;

      vec3 d = vec3(.47, .51, .52), a = d;
      a = mix(a, d * 1.7,
              f(4. / c, 0., F(b)) * (1. - step(n(b), 0.)) * step(0., b.y)),
      a = mix(a, d * 1.6,
              f(4. / c, 0., C(b)) * step(sin(atan(b.x, b.y - .29) - 1.44509), 0.)),
      a = mix(a, d * 1.6, step(n(b), 0.) * f(.05 / c, 0., j(b, 1)) * step(Z(b), 0.) ),
      a = mix(a, d * 1.1, f(4. / c, 0., G(b))),
      a = mix(a, d * 1.4, f(4. / c, 0., Q(b))),
      a = mix(a, d * 1.4, f(.05 / c, 0., j(b, 0)) * step(z(b), 0.)),
      a = mix(a, d * 1.6, f(4. / c, 0., O(b))),
      a = mix(a, d * 1.6, f(.05 / c, 0., j(b, 0)) * step(x(b), 0.)),
      a = mix(a, d * 1.6, f(4. / c, 0., H(b))),
      a = mix(a, d * 1.6, step(J(b), 0.) * f(4. / c, 0., K(b))),
      a = mix(a, d * 1.6, f(4. / c, 0., I(b))),
      a = mix(a, d * 1.6, f(.02 / c, 0., L(b))),
      a = mix(a, d * 1.8, f(4. / c, 0., M(b))),
      a = mix(a, d * 1.8, f(4. / c, 0., S(b))),
      a = mix(a, d * 1.8,
              f(4. / c, 0., abs(mod(b.y + .025, .05) - .025)) * step(v(b), 0.)),
      a = mix(a, d * 1.7, f( (cine ? .02 : .015) / c, 0., D(b + vec2(-.4, 1.15), 4., 3.))),
      a = mix(a, d * 1.7, f(4. / c, 0., abs(w(b)))),
      a = mix(a, d * 1.7, f(.05 / c, 0., j(b, 0)) * step(w(b), 0.));

    // Output to screen
    U = vec4(a, 1);
}
