#define M(C, s) color = mix(color, C, clamp(s, 0., 1.)

float circleSDF(vec2 st, vec2 center) {
    return length(st - center) * 2.0;
}

float fill(float x, float size) {
    float pw = 1.0 / iResolution.x;
    return smoothstep(-pw, pw, size - x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = step(0.5, uv.x) == 0.0 ? vec3(0.506, 0.478, 0.506) : vec3(0.929, 0.725, 0.573);

    vec3 circleColor = vec3(0.69, 0.584, 0.518);

    float MX = iMouse.x / iResolution.x;

    float r = iResolution.x / iResolution.y;

    vec2 offset = vec2(0.17 - MX / 6.0, 0.0);
    float circleLeft = circleSDF(uv * vec2(r, 1.0) + offset, vec2(r * 0.5, 0.5)) * step(uv.x, 0.4 + MX / 10.0);
    float circleRight = circleSDF(uv * vec2(r, 1.0) - offset, vec2(r * 0.5, 0.5)) * step(0.6 - MX / 10.0, uv.x);

    float f = 7.0;
    float nb = 4.0;
    float s = 0.5;

    color = M(circleColor, 1.0 - fill(fract(min(circleLeft * f, nb)), s)));
    color = M(circleColor, 1.0 - fill(fract(min(circleRight * f, nb)), s)));

    fragColor = vec4(color,1.0);
}
