float tick (float t ) {
  float i = floor(t);
  float r = fract(t);
  r = smoothstep(0.,1.,r);
  r = smoothstep(0.,1.,r);
  return r + i;

}
vec3 flit(float t) {

  vec3 x = normalize(vec3(1));
  float t1 = tick(t);
  float t2 = tick(t * .71);
  float t3 = tick(t * .55);
  x.yz *= rot(t1);
  x.xz *= rot(t2);
  x.yx *= rot(t3);

