#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

vec3 ori() {
    vec2 m = iMouse.xy/iResolution.xy;
    float th = tanh(0.4*iTime);
    float y = mix(5., 2., th);
    float z = mix(-5., -3., th);
    vec3 ro = vec3(0, y, z);
    ro.xz *= rot(-0.04*(iTime+25.));
    //ro.yz *= rot(-m.y*3.14+1.);
    //ro.xz *= rot(-m.x*6.2831);
    return ro;
}

vec2 map(vec3 p) {
    float mat = 0.;  
    float t = 0.1 * (iTime + 25.);
    float sc = 0.1;
    float pd = p.y - texture(iChannel0, sc*(p.xz + t)).r;
    pd -= 0.5*texture(iChannel0, 2.*sc*(p.xz-100. + 1.01*t)).r;
    pd += 0.25*texture(iChannel0, 4.*sc*(p.xz +0.99* t)).r;
    float v = mix(7., 1., tanh(0.4*iTime));
    pd = mix(pd, p.y-0.5, 1.-1./cosh(v*length(p.xz)));

    float pd2 = p.y-0.4;//
    if (pd2 < pd) {
        //pd = smin(pd, pd2, 0.12);
        //mat = 1.;
    }
    mat = exp(-4.*(pd+pd2));
    return vec2(0.25*pd, mat);
}

vec3 march(vec3 ro, vec3 rd, float z) {	
    float d = 0.;
    float s = sign(z);
    int steps = 0;
    float mat = 0.;
    for(int i = 0; i < MAX_STEPS; i++) {
    	vec3 p = ro + rd * d;
        vec2 m = map(p);
        //m.x *= 0.8 + 0.2 * hash(hash(p.x,p.z), p.y); // for glow
        if (s != sign(m.x)) { z *= 0.5; s = sign(m.x); }
        if (abs(m.x) < SURF_DIST || d > MAX_DIST) {
            steps = i + 1;
            mat = m.y;
            break;
        }
        d += m.x * z; 
    }

    return vec3(min(d, MAX_DIST), steps, mat);
}

vec3 norm(vec3 p) {
	float d = map(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        map(p-e.xyy).x,
        map(p-e.yxy).x,
        map(p-e.yyx).x);

    return normalize(n);
}

vec3 dir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

float AO(in vec3 p, in vec3 n) {
	float occ = 0.;
    float sc = 1.;
    for (float i = 0.; i < 5.; i++) {
        float h = 0.015 + 0.01 * i;
        float d = map(p+h*n).x;
        occ += (h-d)*sc;
        sc *= 0.95;
    }
    return clamp(1. - 3.*occ, 0., 1.);
}

float shadow(in vec3 ro, in vec3 rd) {
    float res = 1.;
    float t = SURF_DIST;
    for (int i=0; i<24; i++)
    {
		float h = map(ro + rd * t).x;
        float s = clamp(32. * h / t, 0., 1.);
        res = min(res, s);
        t += clamp(h, 0.01, 0.2);
        if(res<SURF_DIST || t>MAX_DIST ) break;
    }
    res = clamp(res, 0.0, 1.0);
    return smoothstep(0., 1., res);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = ori();

    vec3 rd = dir(uv, ro, vec3(0,.9,0), 2.5);
    vec3 col = vec3(0);

    vec3 m = march(ro, rd, 1.);  
    float d = m.x;    
    vec3 p = ro + rd * d;
    vec3 n = norm(p);
    vec3 r = reflect(rd, n);

    if (d<MAX_DIST) {        
        vec3 ld = normalize(vec3(1,2,3));

        float t = 0.1 * iTime;
        ld = normalize(vec3(30.*cos(t),13,30.*sin(t))-p);
        float dif  = dot(n,  ld)*.5+.5;
        float spec = pow(dif, 16.);
        spec = 1.-pow(.5+.5*dot(r, ld), 2.);
        spec = clamp(spec, 0., 1.);
        float fres = pow(1. + dot(rd, n),  5.);    

        // accidentally looks like clouds
        vec3 c1 = texture(iChannel1, 0.01*p.xz + 0.01*iTime).rgb;
        c1 = pow(c1 * vec3(.9,.9,1), vec3(4));
        col = vec3(spec) * clamp((1.-m.z), 0., 1.); 

        col = max(col, c1);
        col = mix(col, vec3(.6,.55,1), 1.-exp(-0.005*m.y));

       // col = vec3(pow(spec, 8.));
        // why not
        col = mix(col, vec3(1,0.5,0.5), pow(AO(p, n), 8.));

        // somehow looks like there's no snow on it
        // (im literally just bashing things together but it's
        //  looking cool)
        col *= .45+.55*clamp(shadow(p+SURF_DIST*n, ld), 0., 1.);
    }

    col = clamp(col, 0., 1.);

    //col = vec3(1. - exp(-0.01 * m.y));
    col = pow(col, vec3(1./2.2));	// gamma correction

    fragColor = vec4(col,1.0);
}

