vec3 palette(float d) {
    vec3 purple = vec3(0.6, 0.2, 0.8); // Purple
    vec3 turquoise = vec3(0.0, 0.7, 0.7); // Turquoise
    vec3 blue = vec3(0.0, 0.2, 0.6); // Blue

    // Interpolate between the colors based on the distance 'd'
    vec3 color = mix(purple, turquoise, d);
    color = mix(color, blue, d);
    return color;
}

vec2 rotate(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return p * mat2(c, s, -s, c);
}

float map(vec3 p) {
    float angle = iTime * 0.05; // Slower rotation speed
    for (int i = 0; i < 14; ++i) { // Increased iterations
        float t = angle;
        p.xz = rotate(p.xz, t);
        p.xy = rotate(p.xy, t * 1.89);
        p.xz = abs(p.xz);
        p.xz -= 0.3;
    }

    float sphere = length(p) - 0.3; // Adjust the radius to make it smaller

    return sphere;
}

vec4 rm(vec3 ro, vec3 rd) {
    float t = 0.1;
    vec3 col = vec3(0.0);
    float d;
    for (float i = 0.3; i < 66.0; i++) {
        vec3 p = ro + rd * t;
        d = map(p) * 0.5;
        if (d < 0.02) {
            break;
        }
        if (d > 140.0) {
            break;
        }
        col += palette(length(p) * 0.1) / (400.0 * d);
        t += d;
    }
    return vec4(col, 1.0 / (d * 100.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - (iResolution.xy / 2.0)) / iResolution.x;
    float spiralTime = iTime * 0.5; // Adjust the speed of the spiral motion
    vec3 ro = vec3(0.0, 0.0, -28.0); // Move even closer to the origin

    // Apply a spiral rotation to ro.xz coordinates
    ro.xz = rotate(ro.xz, spiralTime);

    vec3 cf = normalize(-ro);
    vec3 cs = normalize(cross(cf, vec3(0.0, 4.0, 0.0)));
    vec3 cu = normalize(cross(cf, cs));

    vec3 uuv = ro + cf * 0.7 + uv.x * cs + uv.y * cu; // Scale down the shape

    vec3 rd = normalize(uuv - ro);

    vec4 col = rm(ro, rd);

    fragColor = col;
}
