renderpass.code:
float Circle(vec2 p,vec2 c)
{
    float d = length(p-c);
    return smoothstep(fwidth(d),0.,d-0.02);
}

float Line(vec2 p,vec2 a,vec2 b)
{
    vec2 pa = p-a,ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
    vec2 c = a+ba*t;
    float d = length(p-c);
    return smoothstep(fwidth(d),0.,d-0.001);
}

vec2 Bezier(vec2 a,vec2 b,vec2 c,float t)
{
    return mix(mix(a,c,t),mix(c,b,t),t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;
    vec2 m = (iMouse.xy-0.5*iResolution.xy)/iResolution.x;

    vec3 col = vec3(0.);

    float t = sin(iTime)*0.5+0.5;
    vec2 
        A = vec2(-0.2,-0.1),
        B = vec2(0.2,0.1),
        C = m,
        AC = mix(A,C,t),
        CB = mix(C,B,t),
        ACB = mix(AC,CB,t);

    col += vec3(1,0,0)*Circle(uv,A);
    col += vec3(0,0,1)*Circle(uv,B);
    col += vec3(0,1,0)*Circle(uv,C);
    //col += vec3(1,1,0)*Circle(uv,AC);
    //col += vec3(0,1,1)*Circle(uv,CB);
    col += vec3(1,1,1)*Circle(uv,ACB);

    //col += Line(uv,A,C)+Line(uv,C,B);
    //col += Line(uv,AC,CB);
    int MAX_NUM = 25;
    vec2 p,pp=A;
    for(int i=1;i<=MAX_NUM;i++)
    {
        t = float(i)/float(MAX_NUM);
        p = Bezier(A,B,C,t);
        col += max(col,Line(uv,p,pp));
        pp = p;
    }

    fragColor = vec4(col,1.0);
}
