void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

    // Create a grid of squares
    vec2 p = uv * 5.0;
    vec2 a = fract(p) - 0.5;
    vec2 d = fwidth(p);
    vec2 grid = smoothstep(-d, d, a);

    // Create a circular pattern
    float r = length(uv);
    float angle = atan(uv.y, uv.x);
    float spiral = sin(8.0 * r - 20.0 * angle);

    // Add a blur effect
    float blur = 0.03;
    vec3 blurredColor = vec3(0.0);
    for (float i = -2.0; i <= 2.0; i++) {
        for (float j = -2.0; j <= 2.0; j++) {
            vec2 offset = vec2(i, j) * blur;
            blurredColor += texture(iChannel0, uv + offset).rgb;
        }
    }
    blurredColor /= 25.0;

    // Harmonize the colors
    vec3 harmonizedColor = vec3(0.0);
    harmonizedColor.r = blurredColor.r + 0.25 * blurredColor.g + 0.25 * blurredColor.b;
    harmonizedColor.g = blurredColor.g + 0.25 * blurredColor.r + 0.25 * blurredColor.b;
    harmonizedColor.b = blurredColor.b + 0.25 * blurredColor.r + 0.25 * blurredColor.g;

    // Add an illusion of movement
    vec2 offset = vec2(0.1 * iTime, 0.0);
    vec3 color = vec3(0.0);
    color += grid.x * vec3(1.0, 0.0, 0.0);
    color += grid.y * vec3(0.0, 1.0, 0.0);
    color += spiral * vec3(0.0, 0.0, 1.0);
    color = texture(iChannel0, uv + offset).rgb * 0.5 + color * 0.5;

    fragColor = vec4(harmonizedColor * color, 1.0);
}
