vec2 remap01(vec2 x, vec2 minvalue, vec2 maxvalue){
    return clamp((x - minvalue) / (maxvalue - minvalue), 0.0, 1.0);
}
void circle(inout vec4 fragColor, in vec2 fragCoord,vec2 uv, vec2 center, float radius, vec3 color){

 float d_ = length (center - uv);
     if (d_ <= radius){
        fragColor = vec4 (color,1.0);
    }

}
vec2 rotation1(vec2 center,in vec2 fragCoord,float rotation){

    vec2 right = vec2 (-cos(rotation), sin(rotation));
    vec2 up = vec2(-right.y,right.x);

    vec2 asd = fragCoord - center;
    float x = dot(asd, right);
    float y = dot(asd, up);
    return vec2(x,y);


}
float radian (float angle){

float rad = angle / 180.*3.14159265;
return rad;

}
void line(vec2 uv,inout vec4 fragColor, in vec2 fragCoord, vec2 center, vec2 extent,float angle){

