// variant of https://shadertoy.com/view/mlKczd
// using tilable bluenoise of https://www.shadertoy.com/view/4dSyDR
// variant of https://shadertoy.com/view/dtKczd

float hash( uint n ) { // from https://www.shadertoy.com/view/llGSzw  Base: Hugo Elias. ToFloat: https://iquilezles.org/articles/sfrand
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.;
}
vec2 M = vec2(128.);
vec4 noise(vec2 U) {
    U = mod(U,M.x) + M.y; // avoids grid glitch by doing modulo here
    U-=.5; return vec4( hash(uint(U.x+iResolution.x*U.y)) );
}

vec4 H(vec2 U) {
#define V(i,j)  noise(U+vec2(i,j))
  //U=floor(U/8.); 
    vec4 N = 8./9.* noise( U ) 
           - 1./9.*( V(-1,-1)+V(0,-1)+V(1,-1) +V(-1,0)+V(1,0) +V(-1,1)+V(0,1)+V(1,1) );  
    return N*2. + .5;   // or *1 to avoid saturation at the price of low contrast
}

//#define H(v)     fract(1e3*sin(dot(v,vec2(73,91))))          // hash

void mainImage( out vec4 O, vec2 u )
{
    u /= 128.;
    int  h = iFrame/180 % 2 > 0 ? int(8.*H(floor(u))) : 0;   // rand in {0,...7}
    vec2 U = fract(u);                                       // local frame
    O *= 0.;
    if ( iMouse.z>0. && min(U.x,U.y)<2./128. ) O.r++;        // if click draw tiles borders
    if ( h/4   > 0 ) U = U.yx;                               // rand bit3: symmetry
    if ( h/2%2 > 0 ) U = 1.-U;                               // rand bit2: rot Pi
    if ( h%2   > 0 ) U = vec2(1.-U.y,U);                     // rand bit1: rot Pi/2
    O += vec4( H(U*128.).x  );                                 // draw random dots (same in each tile)
  }
