// ray casting bounding shape
const float CAM_DIST = 7.;
const float CAM_FLE = 1.3;
const float BB_DIST = 4.;
const float RAY_MIN_DIST = 0.5;
const float RAY_MAX_DIST = CAM_DIST+1.1*BB_DIST;

int   MAX_MARCHING_STEPS = 310;
const float RAY_STEP_FACTOR3 = 230./450.;
float RAY_STEP_FACTOR  = (0.5-0.0001)* pow(RAY_STEP_FACTOR3, 3.) + 0.0001;
float RAY_PRECISION = 1.;

const float PS_a0 = 12.;

// fog
const bool  FOG_ON = true;
const float FOG_DENSITY = 2.5E-3;

// data from sdf
vec4 mat = vec4(0);

float map(in vec3 p) {
    float rxy = 0., au = 0.0, av = 0.0;
    float cell_id = 0., sr = 0.; //sens of rotation, sig(sr): rotation direction, abs(sr) : number edges
    vec2 z = vec2(0.);

    /// platonic sphere
    // normalize spherical radius
    vec3 pn = normalize(p);

    // projection from n cells to 1 cell.
    pn = tetra_cells(pn, cell_id, sr);

    // inverse stereographic projection
    z = pn.xy/(1.-pn.z);
    z = cTeraPolyArc2disk(z);

    // r-polygon-distribution
    vec2 e = vec2(1.8, 0.5);
    e.y += 0.1 - 0.1 *cos(3.*atan(z.y, z.x));
    float en = 1.+( (PS_a0<0.?e.x:e.y)-1. )*smoothstep(1.,5., abs(PS_a0));        
    float r = length(z); vec2 zn = z/r;
    z = dist(r, en) * zn;     

    // stereograpic projection
    float zz = dot(z,z); pn = vec3(2. * z.x,  2. * z.y, -1. + zz); pn /= 1.+zz;

    // unnormalize spherical radius
    p = length(p) * pn;

    /// torus,sphere,2spheres
    // 3D-space: cartesian3D => cylinder3D transformation
    rxy = length(p.xy); au = atan(p.y, p.x);

    // 2D-space: complex plane := radial plane in cylinder 3d coordinates  
    z = vec2(rxy, p.z);

    // 2Djulia 
    z = cmul(z,z); z.x -= PS_a0;

    p = vec3(log(length(z)), 0.5*au/PI, 0.5*atan(z.y,z.x)/PI); // r,u,v
    z = p.yz; // torus uv

    /// truchet 3d
    // repetition
    float nu = 3.*2.*6.;
    float nv = 2.*2.;
    float nr = 1.;

    p *= vec3(nr,nu,nv); // r,u,v
    p.yzx = p.xyz; 

    ivec3 l0 = -ivec3(nv/2.,0,nu/2.); ivec3 l1 = ivec3(nv/2.,nr,nu/2.)-ivec3(1.);
    vec3 tc_id = floor(p);
    tc_id = clamp(tc_id, vec3(l0), vec3(l1));
    vec3 q = p - tc_id -0.5;

    // random
    float rnd = hash21(tc_id.xz);  

    // flip cell direction randomly
    if(rnd<.5) q.x = -q.x;

    // sdf
    float r0 = 0.5; float r1 = 0.125;
    float d;
    if(fract(rnd*77.57+iTime/6.)>.75) {
       // offset-xz circle, id diagonal sub domain
       vec3 q2 =  length(q.xz-.5)<length(q.xz+.5)
                ? vec3(q.xz-.5,0.)  
                : vec3(q.xz+.5,1.);
       d = sdTorus( vec3(q2.x, q.y, q2.y) , vec2(0.5,r1) );
    }
    else {
       d = opSmoothUnion( sdCylinder( q.zxy, r1 ), sdCylinder( q.xzy, r1 ), 0.05 );
    }

    //d = opIntersection(d, sdBox( q, vec3(r0*0.9))); // TEST: cell BB
    //d = sdSphere( q, r0 ); // TEST: cell unequal scaling

    // cell alpha    
    float tc_a = sdBox( q, vec3(r0*0.9)); float px = fwidth(tc_a);
    tc_a = smoothstep(px,-px,tc_a);

    /// return
    // torus uv, truchet cell alpha, random cell id
    mat.xy = z; mat.z = tc_a, mat.w = rnd;    
    return d;
}

// intersect
float intersect(in vec3 ro, in vec3 rd, in float px) {
    float res = -1.0; // init no intersection

    // bounding shape BB
    vec2 dis = isphere( ro, rd , abs(BB_DIST));

    if( dis.y<0.0 ) return -1.0; // check BB behind came

    dis.x = max( dis.x, RAY_MIN_DIST ); // check min ray length
    dis.y = min( dis.y, RAY_MAX_DIST ); // check max ray length

    // raymarch signed distance field    
	float fh = RAY_STEP_FACTOR;
    float t = dis.x; // ray starts at boundingbox
    for( int i=0; i<MAX_MARCHING_STEPS; i++  ) {
        vec3 pos = ro + rd*t;
        float th = RAY_PRECISION * px * t;
        float h = map( pos);
        if( t>dis.y || h<th ) break; // ray outside BB or hit
        t += h*fh;
    }
    if( t<dis.y ) {
        res = t;
    }
    return res;
}

vec3 calcNormal( in vec3 p, in float prec ) {
    vec2 e = vec2( prec, 0. );
    return normalize(vec3(
		map(p+e.xyy) - map(p-e.xyy),
		map(p+e.yxy) - map(p-e.yxy),
		map(p+e.yyx) - map(p-e.yyx) )
    	);
}

vec3 transform(in vec3 p, in float time) { 
	if (iMouse.x > 0.0) {
        vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
		p.yz = crot(p.yz,-PI*m.y);
		p.zx = crot(p.zx,-PI*m.x);
	}
    p.yz = crot(p.yz,-2.*PI*1.*time/360.);
    p.zx = crot(p.zx,-2.*PI*3.*time/360.);
	return p.xyz;
}

// textures
vec4 ribbon(in float uv_x, in float time) {
    float sdf = -(2.*pulsecollision(1.,1./3. * uv_x, time, 2./6.)-1.);
    float px = fwidth(sdf);
    return vec4( h2rgb(sdf -2./6.)
               , smoothstep(px+0.1,-px-0.1,sdf+0.2) );
}

vec4 truchet3D(in float rnd, in float a ,in float time) {
    return mix(vec4(1.), vec4(h2rgb( fract(abs(rnd)*22.93 + time)),1.), a);
}

// main
void mainImage(out vec4 O, in vec2 fragCoord) {  
    // anti-aliasing to take a picture
    vec3 tot = vec3(0.0);
    #define AA 1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {       
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 uv = (2.0*(fragCoord+o) - iResolution.xy)/iResolution.y;

        // camera viewport --> worldspace
        vec3 ro = vec3(0,0,-CAM_DIST);
        vec3 rd = vec3(-uv.x,uv.y,CAM_FLE);
             ro = transform(ro,iTime);
             rd = normalize(transform(rd,iTime));

        // distance hit ray surface
        float px = 2.0/( iResolution.y*CAM_FLE );
        float t = intersect( ro, rd, px );

        //vec3 sun_col = vec3(1.,1.,0.8);
        vec3 sky_col = mix(vec3(0.714,0.145,0.318), vec3(0.000,0.482,1.000), rd.y*.5+.5);

        // background
        vec4 C = vec4(sky_col,1.);
        O = C;

        // object surface
        if (t >= 0.) {
            // surface properties
            vec3 p = ro + rd * t;
            vec3 n = calcNormal( p, 0.001);

            // uv-coords
            vec2 mat_uv = mat.xy;

            // draw texture layers from back to front
            O = vec4(0);
            #if 1
            if(O.a<1.) {             
                C = ribbon( 3.*6.* mat_uv.x, 0.0125*iTime); C.rgb *= 0.7;
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
            #endif
            #if 1
            if(O.a<1.) {
                C = vec4(vec3(n)*0.5,0.2); // normal
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
            #endif            
            #if 1
            if(O.a<1.) {            
                C = truchet3D(mat.w, mat.z, iTime/60.); C.rgb *= 0.5;
                O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
            #endif
            C = O;

            // lighting
            vec3  came_li_o   = transform(vec3(-10.,10.,-10.),iTime); // origine
            vec3  came_li_dir = normalize(p - came_li_o);             // direction
            vec3  came_li_col = vec3(1.,1.,.5);                       // color
            float came_li_shi = 10.;                                  // shininess            

            float cosa = 1.;
            O.rgb += 0.1 * cosa * C.rgb * sky_col;           // amb
            cosa = clamp(dot(-came_li_dir, n), 0., 1.);
            O.rgb += 0.5 * cosa * C.rgb * came_li_col;       // dif       
            cosa = pow(clamp(dot(reflect(came_li_dir, n), -rd), 0., 1.), came_li_shi);           
            O.rgb += 0.4 *(cosa>0.? cosa:0.) * came_li_col ; // spec

            // fresnel
            O.rgb += pow(clamp(1. - dot(n, -rd), 0., 1.), 5.) * 0.6 * sky_col;

            // fog
            if(FOG_ON)
                O.rgb = mix(O.rgb, sky_col, 1.0 - exp(-FOG_DENSITY * pow(t,3.)));    
        }
        tot += O.rgb;
    }
    tot /= float(AA*AA);

    // gamma correction
    O = vec4(rgb2srgb(tot),1.);
}

// References:
//   https://people.maths.ox.ac.uk/trefethen/lightning.html
//   https://iquilezles.org/

// const
const float PI = abs(atan(0.,-1.));

/// 1D transformations
float pow2( in float x){ return x*x;}

float pulsecollision(in float pf, in float x, in float x0, in float pw) {
    // x  : periodic in[0,1]
    // x0 : offset
    // pw : pulse width in[0,1]
    // pf : pulse frequency
    float ntriw_p = abs( fract( pf*(x - x0) )-0.5); // x_normalized triangle wave function moving in +x_direction
    float ntriw_n = abs( fract( pf*(x + x0) )-0.5); // x_normalized triangle wave function moving in -x_direction
    float pp = smoothstep( 0.5*pw, 0., ntriw_p );   // pulse moves in +x_direction
    float pn = smoothstep( 0.5*pw, 0., ntriw_n );   // pulse moves in -x_direction
    return (pp+pn); // in[0.,2.]
    //return (pp+pn)-(pp*pn);// in[0.,1.]
}

float hash21(vec2 p) {
    return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453);
}

/// 2D transformations
/// complex
vec2 cmul(vec2 za,vec2 zb) {
    return za*mat2(zb.x,-zb.y,zb.yx);}

vec2 crot(vec2 z,float a) {
    return cmul(vec2(cos(a),sin(a)),z);}

vec2 cinv(vec2 z) {
    return z*vec2(1,-1)/dot(z,z);}

vec2 cdiv(vec2 z, vec2 w) {
    return cmul(z,cinv(w));}

// polynomial barycentric approximation
vec2 cTeraPolyArc2disk(vec2 z) {
    const int NUM_j = 15;
    const vec2[NUM_j] rf = vec2[]
    (vec2(-0.4534, -0.8913)
    ,vec2( 0.5001,  0.8660)
    ,vec2(-1.0000,  0.0001)
    ,vec2( 0.5008, -0.8656)
    ,vec2(-0.5452,  0.8383)
    ,vec2( 0.1901,  0.9818)
    ,vec2( 0.9875, -0.1576)
    ,vec2(-0.9856, -0.1691)
    ,vec2( 0.4224, -0.9064)
    ,vec2( 0.5738,  0.8190)
    ,vec2(-0.9995,  0.0311)
    ,vec2( 0.4858,  0.8741)
    ,vec2( 0.5760, -0.8175)
    ,vec2(-1.0000, -0.0061)
    ,vec2( 0.4959, -0.8684));

    const vec2[NUM_j] rw = vec2[]
    (vec2( 0.2728,  0.0   )  
    ,vec2( 0.0003,  0.0126)
    ,vec2(-0.0016, -0.0121)
    ,vec2(-0.0172,  0.0324)
    ,vec2( 0.1417, -0.4629)
    ,vec2(-0.4681, -0.2288)
    ,vec2(-0.2433,  0.2822)
    ,vec2( 0.1996, -0.0082)
    ,vec2( 0.2884, -0.0353)
    ,vec2(-0.0666,  0.1824)
    ,vec2(-0.0357, -0.1136)
    ,vec2(-0.1091,  0.0396)
    ,vec2(-0.0831,  0.2627)
    ,vec2( 0.0598, -0.0284)
    ,vec2( 0.0597,  0.0760));

    const vec2[NUM_j] rz = vec2[]
    (vec2(-0.239570, -0.459171)
    ,vec2( 0.354172,  0.611300)
    ,vec2(-0.706487,  0.001072)
    ,vec2( 0.356785, -0.606740)
    ,vec2(-0.277869,  0.437059)
    ,vec2( 0.047259,  0.573454)
    ,vec2( 0.515842, -0.066306)
    ,vec2(-0.591622, -0.166501)
    ,vec2( 0.221135, -0.605193)
    ,vec2( 0.413545,  0.494105)
    ,vec2(-0.671697,  0.057599)
    ,vec2( 0.309337,  0.611574)
    ,vec2( 0.414577, -0.491758)
    ,vec2(-0.695342, -0.019940)
    ,vec2( 0.334074, -0.612218));

    // polynomial barycentric representation
    vec2 zn = vec2(0.); vec2 zd = vec2(0.);       
    for(int j = 0; j<NUM_j; j++)
    {
        vec2 zj = cdiv( rw[j], (z-rz[j]));
        zd += zj; // sum denominator
        zn += cmul(rf[j], zj); // sum nominator
    }
    return cdiv(zn, zd);;
}

// map spherical platonic solid pyramidal sectors to one sector located at -z-axis.
vec3 tetra_cells(in vec3 p, out float cell_id, out float sr){
	//// tetrahedron (bounded by a cube)
	// cells: A,B,C,D
	// cell walls defined by the face_normal
    bool nBA = dot(vec3( 1., 0.,-1.),p)>=0.;
	bool nCA = dot(vec3( 0., 1.,-1.),p)>=0.;
	bool nDA = dot(vec3( 1., 1., 0.),p)>=0.;
	bool nCB = dot(vec3(-1., 1., 0.),p)>=0.;
	bool nDB = dot(vec3( 0., 1., 1.),p)>=0.;
	bool nDC = dot(vec3( 1., 0., 1.),p)>=0.;

    // map n-cells to 1-cell by rotation
    // rotation angles
    const float a45 = PI/4.;             // 45° 
    const float a35 = atan(sqrt(8.))/2.; // 35.264°
    const float a30 = PI/6.;             // 30°

    const vec2 ei_a45 = vec2(cos(a45), sin(a45));
    const vec2 ei_a35 = vec2(cos(a35), sin(a35));
    const vec2 ei_a30 = vec2(cos(a30), sin(a30));

    vec2 ei_y = ei_a45;   
    vec2 ei_x = ei_a35;
    vec2 ei_z = ei_a30;
    // symmetries
    if(nBA && nCA && nDA) //inside cell A
	{
		cell_id =  0.;
        ei_x.y *= -1.; // a = -35.264°
	}
	else if( nCB && nDB && !nBA) //inside cell B
	{
		cell_id =  1.;
		ei_y   *= -1.; // a = -3.*45°
        ei_x.y *= -1.; // a = -35.264°
	}
	else if(!nCA && !nCB && nDC) //inside cell C
	{
		cell_id =  2.;
		ei_y.x *= -1.; // a = +3.*45°
		ei_z.y *= -1.; // a = -30°
	}
	else if(!nDA && !nDB && !nDC)//inside cell D
	{
		cell_id =  3.;
		ei_y.y *= -1.; // a = -45°
		ei_z.y *= -1.; // a = -30°
	}
	p.zx = cmul(p.zx,ei_y); // y-axis
	p.yz = cmul(p.yz,ei_x); // x-axis
	p.xy = cmul(p.xy,ei_z); // z-axis
	// 3 edges per face, non cyclic
	sr = 3.;
    return p;
}

// distribution
float sig(float x) {
    return x < 0. ? -1. : +1.; 
    }

float dist(float x, float e) {
    // e in [0., +inf]
    return sign(x)*abs( pow(abs(x), abs(e)) );
    }

vec2 r_poly_dist(vec2 z, vec2 e, float sh_a) {   
    /// r-polygon-distribution
    float r = length(z); z = z/r; // TODO: test division by zero
    float d0 = 0.5 *abs(e.y-e.x);
    float e2 = e.x + d0 + d0 * (2.*smoothstep(-1., 1., -sh_a) - 1.);
    //    float e2 = e.x + d0 * 2. * smoothstep(-1., 1., -sh_a);
    return dist(r, e2) * z;
}

// bb
vec2 isphere(in vec3 ro, in vec3 rd, in float r) {
	float b = dot(ro,rd);
	float c = dot(ro,ro) - r*r;
    float h = b*b - c;   
    if( h<0.0 ) return vec2(-1.0);
    h = sqrt( h );
    return -b + vec2(-h,h);
}

// sdf
float opIntersection( float d1, float d2 ) {
    return max(d1,d2); }

float opSmoothUnion( in float d1, in float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere( vec3 p, float s ) {
  return length(p)-s;
}

float sdBox( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCylinder( vec3 p, float r ) {
  return length(p.xz)-r;
}

float sdTorus( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// color
vec3 h2rgb(float h){
    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return rgb*rgb*(3.0-2.0*rgb); // cubic smoothing
}

vec3 hsv2rgb(float h, float s, float v){     
    return v * mix( vec3(1.0), h2rgb(h), s); // hue, saturation, value
}

// srgb <--> rgb (linear)
vec3 srgb2rgb(vec3 col) {
    return pow(col, vec3(2.2));
}

vec3 rgb2srgb(vec3 col) {
    return pow(col, vec3(1./2.2));
}
