///////////////////////////////////////////////////////////////////////
//
// Random Paths, mla, 2022
//
// Apply a simple random path rewriting automaton to a closed path.
// Mouse resets (eg. after a window resize).
//
// Next step is to apply some image-based weighting to the randomization.
//
// Path is stored in Buffer A - the x and y components of pixel 
// (i,j) indicate the presence or absence of an edge to (i+1,j), (i,j+1)
// respectively.
//
// There are two types of rewrite, applied to a 2x2 grid:
//
// xxxx     x..x    xxxx     ...x
// .... <-> x..x    x... <-> ...x
// ....     x..x    x...     ...x
// ....     xxxx    x...     xxxx
//
// Sadly, the algorithm doesn't guarantee that the path remains connected if
// there is a cross pattern - the wrong side of the cross can get flipped
// by the second rewrite:
//
// ..x..    ..xxx
// ..x..    ....x
// xxxxx -> xxx.x
// ..x..    ..x..
// ..x..    ..x..
//
// I'm not sure what can be done about this without considerably
// complicating the algorithm.
//
///////////////////////////////////////////////////////////////////////

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  vec2 p = 1.1*(2.0*fragCoord-iResolution.xy)/iResolution.y;
  p = 0.5*(p+1.0); // Move to [0,1] square
  ivec2 vertex = clamp(ivec2(round(float(N)*p)),ivec2(0),ivec2(N));
  float px = fwidth(p.x), d = 1e8;
  // The 4 segments coming off this vertex - the buffer entries
  // indicate the presence of rightwards and downwards segments.
  bvec4 a = bvec4(texelFetch(iChannel0,vertex-ivec2(1,0),0).x,
                  texelFetch(iChannel0,vertex-ivec2(0,1),0).y,
                  texelFetch(iChannel0,vertex,0).xy);
  // I don't usually like this sort of macro, but this one is handy.
  #define S(p,q,r) (segment((p),vec2(q)/float(N),vec2((q)+(r))/float(N)))
  if (vertex.x > 0 && a.x) d = min(d,S(p,vertex,-ivec2(1,0)));
  if (vertex.y > 0 && a.y) d = min(d,S(p,vertex,-ivec2(0,1)));
  if (vertex.x < N && a.z) d = min(d,S(p,vertex, ivec2(1,0)));
  if (vertex.y < N && a.w) d = min(d,S(p,vertex, ivec2(0,1)));
  vec3 col = vec3(1,1,0.8);
  vec3 tex = texture(iChannel1,fragCoord/iResolution.y).xyz;
  col *= 0.75+0.25*tex;
  col *= vec3(smoothstep(0.0,px,d-0.1*length(tex)/float(N)));
  col = pow(col,vec3(0.4545));
  if (alert) col.x = 1.0;
  fragColor = vec4(col,1);
}

void mainImage(out vec4 result, vec2 fragCoord) {
  ivec2 vertex = ivec2(fragCoord);
  result = texelFetch(iChannel0,vertex,0);
  if (any(lessThan(vertex,ivec2(0))) || any(greaterThan(vertex,ivec2(N)))) {
    result = vec4(0);
  } else if (iFrame == 0 || iMouse.z > 0.0 || result.w != 1234.0) {
    result = vec4(vertex.y == 0 || vertex.y == N,
                  vertex.x == 0 || vertex.x == N,
                  0,1234);
  } else {
    // Consider a 2x2 grid of vertices, with segments possible between each
    // horizontal and vertical pair of vertices.
    int n = irand(iFrame+1,4);
    ivec2 offset = ivec2(n/2,n%2);
    if (all(greaterThanEqual(vertex,offset)) && all(lessThan(vertex,N+offset))) {
      ivec2 base = (vertex-offset)/2*2+offset;
      bvec4 a = bvec4(texelFetch(iChannel0,base,0).xy,
                      texelFetch(iChannel0,base+ivec2(0,1),0).x,
                      texelFetch(iChannel0,base+ivec2(1,0),0).y);
      if (any(a) && !all(a) && a != bvec4(1,0,1,0) && a != bvec4(0,1,0,1)) {
        if (vertex == base) result.xy = vec2(not(a.xy));
        if (vertex == base+ivec2(0,1)) result.x = float(!a.z);
        if (vertex == base+ivec2(1,0)) result.y = float(!a.w);
      }
    }
  }
}

int N = 100;

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing       
  return v * mix( vec3(1.0), rgb, s);
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint ihash(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

int irand(int i, int range) {
 return int(ihash(uint(i))%uint(range));
}

float rand(int i, int j) {
  uint h = ihash(uint(31*i+j));
  return float(h)/pow(2.0,32.0);
}

float rand(int i) {
  uint h = ihash(uint(i));
  return float(h)/pow(2.0,32.0);
}

float segment(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
}
