// Inspiration: https://www.shadertoy.com/view/wlsyDl

#ifndef PI
#define PI 3.1415926535897932384626433832795
#endif
#ifndef TAU
#define TAU 6.2831853071795864769252867665590
#endif

#define DIVS 19.0
#define CIRCLES 0.0
#define rot(a)   mat2( cos(a), -sin(a), sin(a), cos(a) )



// @author sam hocevar
// http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)  / iResolution.y;

    float c = 0.;

    // timer [0; 1]
    float t = iTime / 60.;
    if (t > .5) {
        uv*= rot((t-.5) * 10.);
    }
    t = min(1., t);


    // the grid
    vec2 dv = uv * DIVS + .5; // we add .5 for odd centering
    vec2 gd = fract(dv) - .5;
    vec2 id = floor(dv);

    // checkboard
    float dm = mod(id.x + id.y, 2.);

    // hue
    float hue = mix(0.22, dm, 0.35);

    // manhattan distance
    float mand = sin(((abs(id.x) + abs(id.y)) / DIVS * 2. - iTime*(0.1+t*.4)) * 1.5) * .5;


    vec3 color = vec3(hue, .025, mix(mand, 0.8 - mand, dm));


    // the border, source of all motion
    float bd = smoothstep(0.35, 0.5, abs(gd.x));
    bd = max(bd, smoothstep(0.35, 0.5, abs(gd.y)));
    float a = atan(gd.y, gd.x)*2. + cos(iTime * .5) * (t*10.+3.) + (mod(id.x, 2.)*4.-2.) * cos(iTime);
    a = mod(a, 4.*PI);
    color = mix(color, color*pow((cos(a)*.5+.5), 2.), bd);


    // adding circles
    float cir = smoothstep(0.3, 0.20, length(gd));
    color-= cir * CIRCLES;


    fragColor = vec4(color, 1.);
}
