#define PI 3.141592653589793

float random(vec2 uv){
return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;
uv.x *= iResolution.x/iResolution.y;

// Animación del tiempo
float time = iTime * 0.5;

// Patrón 1
vec2 p1 = fract(sin(uv + time) * 10.0);

// Patrón 2
vec2 p2 = fract(uv * 5.0 + sin(time + 100.0));

// Combinar patrones

vec2 p = mix(p1, p2, 0.5);
p += random(fragCoord + vec2(time)) * 0.1;

// Animación de la distancia
float d = length(p);
d += sin(d * 3.0 + time) * 0.5;

float cosValue = cos(d * 2.0 + p.x + time);
vec3 col = 0.5 + 0.5 * vec3(cosValue);

// Agregar variación de color
col += random(fragCoord) * 0.2;

fragColor = vec4(col, 1.0);
}
