//Note that both of these can work with native mipmapping (no textureGrad or textureLod needed).
//Both techniques also use exclusively int hashes, which means they will perform the same way regardless of floating point
//shenanigans. For example, they'll perform the same between the GPU and the CPU. This is different from most techniques that
//rely on vec3 hash33(vec3) and such, where the result of the hash is unstable to small pertrubations.

vec2 GetFlowField(vec2 pixel_pos, vec2 focus_point)
{
    vec2 delta = pixel_pos - focus_point;
    vec2 n = delta / (length(delta) + iResolution.x / 10.0f);
    float l = length(delta) / (iResolution.x / 2.0f);
    return 
        100.0f * n +
        cos(length(delta) / (iResolution.x / 10.0f)) * vec2(-n.y, n.x) * iResolution.x / 10.0f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixel_pos = fragCoord.xy;
    vec2 pixel_velocity = GetFlowField(pixel_pos, iMouse.xy);

    vec3 linear_col = vec3(0.0f);
    vec2 moments = vec2(0.0f);

    float grid_step2 = 0.5f;
    float grid_step3 = iResolution.x / 15.0f;

    float uv_tiling = iResolution.x / 20.0f;
    float debug_pos = (sin(iTime) * 0.5f + 0.5f) * iResolution.y;

    bool mouse_was_pressed = texelFetch(iChannel2, ivec2(0), 0).r > 0.5f;
    float mouse_pressed_time = texelFetch(iChannel2, ivec2(0), 0).g;

    float debug_draw = mix(
        1.0f - clamp((pixel_pos.y - debug_pos) / 10.0f - 0.5f, 0.0f, 1.0f),
        clamp(mouse_pressed_time, 0.0f, 1.0f),
        mouse_was_pressed);


    int quad_index = 0;
    float width = 2.0f;
    if(pixel_pos.x < iResolution.x * 0.5f - width)
        quad_index = 1;
    if(pixel_pos.x > iResolution.x * 0.5f + width)
        quad_index = 2;

    if(quad_index == 1)
    {
        float time_pos = iTime;
        vec2 gyroid_pos = fragCoord.xy / (iResolution.x * 0.05f);
        float smooth_noise = sin(gyroid_pos.x) * cos(gyroid_pos.y) + sin(gyroid_pos.y) + cos(gyroid_pos.x);
        float time_offset = smooth_noise * grid_step2 * 0.5f;
        InterpNodes2 interp_nodes2 = GetLinearInterpNodes(time_pos + time_offset, grid_step2);
        for(int j = 0; j < 2; j++)
        {
            vec3 node2_hash = hash3i3f(ivec3(0, 0, interp_nodes2.indices[j]));
            float time_delta = time_pos - (interp_nodes2.positions[j]);

            vec2 uv = Rotate2(pixel_pos + pixel_velocity * time_delta, 2.0f * pi * node2_hash.x) / uv_tiling + node2_hash.yz;
            float weight = interp_nodes2.weights[j];
            linear_col += SrgbToLinear(texture(iChannel0, uv).rgb) * weight * mix(1.0f, (j > 0) ? 0.0f : 1.0f, debug_draw);
            moments += vec2(weight, weight * weight);
        }
    }
    if(quad_index == 2)
    {
        InterpNodes3 interp_nodes3 = GetHexGridInterpNodes(pixel_pos, vec2(grid_step3));
        for(int i = 0; i < 3; i++)
        {
            vec3 node3_hash = hash3i3f(interp_nodes3.indices[i].xyy);

            float time_pos = node3_hash.x * grid_step2 + iTime;
            InterpNodes2 interp_nodes2 = GetLinearInterpNodes(time_pos, grid_step2);

            vec2 pixel_delta = pixel_pos - interp_nodes3.positions[i];
            vec3 debug_col = vec3(0.0f);
            debug_col[i] = 1.0f;
            for(int j = 0; j < 2; j++)
            {
                vec3 node2_hash = hash3i3f(ivec3(interp_nodes3.indices[i].xy, interp_nodes2.indices[j]));
                float time_delta = time_pos - interp_nodes2.positions[j];
                vec2 uv = Rotate2(pixel_delta + pixel_velocity * time_delta, 2.0f * pi * node2_hash.x) / uv_tiling + node2_hash.yz;
                float weight = interp_nodes3.weights[i] * interp_nodes2.weights[j];
                linear_col += SrgbToLinear(texture(iChannel0, uv).rgb) * weight * mix(1.0f, (j > 0 || i > 0) ? 0.0f : 1.0f, debug_draw);
                moments += vec2(weight, weight * weight);
            }
        }
    }    

    vec3 mean_col = SrgbToLinear(textureLod(iChannel0, vec2(0.5f), 10.0f).rgb);
    vec3 linear_res = mix(PreserveVariance(linear_col, mean_col, moments.y), linear_col, debug_draw);
    fragColor = vec4(LinearToSrgb(linear_res), 1.0f);
    //InterpNodes2 interp_nodes2 = GetLinearInterpNodes(fragCoord.x, 200.0f);
    //fragColor = vec4(abs(interp_nodes2.positions[1] - fragCoord.x) / 20.0f);
    //fragColor = vec4(interp_nodes2.weights[1]);
}

//Inspired by https://www.shadertoy.com/view/dtySDy

//http://www.jcgt.org/published/0009/03/02/paper.pdf
uvec3 hash33UintPcg(uvec3 v)
{
    v = v * 1664525u + 1013904223u;
    v.x += v.y*v.z; v.y += v.z*v.x; v.z += v.x*v.y;
    //v += v.yzx * v.zxy; //swizzled notation is not exactly the same because components depend on each other, but works too

    v ^= v >> 16u;
    v.x += v.y*v.z; v.y += v.z*v.x; v.z += v.x*v.y;
    //v += v.yzx * v.zxy;
    return v;
}

vec3 hash3i3f(ivec3 seed)
{
    uvec3 hash_uvec3 = hash33UintPcg(uvec3(seed));
    return vec3(hash_uvec3) * (1.0f / float(~0u));
}

const float pi = 3.141592f;

//Amazing resource explaining cube coordinates of hexagon grids:
//https://www.redblobgames.com/grids/hexagons/more-pixel-to-hex.html#justin-pombrio
mat2 skewed_to_grid = mat2(1.0f, 0.0f, 0.5f, sqrt(3.0f) / 2.0f);

vec2 GridToSkewed(vec2 grid_pos)
{
    return inverse(skewed_to_grid) * grid_pos;
}

vec2 SkewedToGrid(vec2 skewed_pos)
{
    return skewed_to_grid * skewed_pos;
}

vec3 SkewedToCube(vec2 skewed_pos)
{
    return vec3(skewed_pos.xy, -skewed_pos.x - skewed_pos.y);
}

//https://justinpombrio.net/programming/2020/04/28/pixel-to-hex.html
vec3 CubeToTri(vec3 cube_pos)
{
    return floor(cube_pos.xyz - cube_pos.zxy);
}

vec3 TriToHex(vec3 tri_index)
{
    return round((tri_index.xyz - tri_index.yzx) / 3.0f);
}

float max3(vec3 v)
{
    return max(max(v.x, v.y), v.z);
}
float HexOuterDist(vec3 cube_delta)
{
    return 1.0f - max3(abs(cube_delta.xyz - cube_delta.yzx));
}

float HexInnerDist(vec3 cube_delta)
{
    return length(cube_delta) / (sqrt(2.0f) / sqrt(3.0f)); //(sqrt(2.0f) / 2.0f) for inscribed normalization
}

struct InterpNodes3
{
    vec2 positions[3];
    ivec2 indices[3];
    vec3 weights;
};

InterpNodes3 GetHexGridInterpNodes(vec2 pos, vec2 grid_step)
{
    vec2 hex_size = grid_step * sqrt(3.0f);
    InterpNodes3 interp_nodes3;
    for(int i = 0; i < 3; i++)
    {
        vec2 grid_pos = pos / hex_size;
        vec2 skewed_offset = vec2(float(i) / 3.0f);
        vec2 skewed_pos = GridToSkewed(grid_pos) + skewed_offset;
        vec3 cube_pos = SkewedToCube(skewed_pos);
        vec3 hex_index = TriToHex(CubeToTri(cube_pos));
        vec2 hex_skewed_pos = hex_index.xy - skewed_offset;
        vec2 hex_grid_pos = SkewedToGrid(hex_skewed_pos);
        interp_nodes3.positions[i] = hex_grid_pos * hex_size;
        interp_nodes3.indices[i] = ivec2(hex_index.xy) * ivec2(3, 1) + ivec2(i, 0);
        interp_nodes3.weights[i] = HexOuterDist(cube_pos - hex_index);
    }
    return interp_nodes3;
}

mat2 Rotate2Mat(float ang)
{
    return mat2(cos(ang), sin(ang), -sin(ang), cos(ang));
}

vec2 Rotate2(vec2 vec, float ang)
{
    return Rotate2Mat(ang) * vec;
}

struct InterpNodes2
{
    vec2 positions;
    ivec2 indices;
    vec2 weights;
};
InterpNodes2 GetLinearInterpNodes(float position, float grid_step)
{
    float grid_pos = position / grid_step;
    vec2 global_phases = vec2(grid_pos * 0.5f) + vec2(0.5f, 0.0);
    vec2 indices = floor(global_phases) * 2.0f + vec2(0.0f, 1.0f);
    vec2 weights = vec2(1.0f) - abs(grid_pos - indices);
    return InterpNodes2(indices * grid_step, ivec2(indices), weights);
}

vec3 SrgbToLinear(vec3 srgb_col)
{
    return pow(srgb_col.rgb, vec3(2.2f));
}

vec3 LinearToSrgb(vec3 linear_col)
{
    return pow(linear_col.rgb, vec3(1.0f / 2.2f));
}

//Qizhi Yu, Fabrice Neyret, Eric Bruneton, and Nicolas Holzschuch. 2011. 
//Lagrangian Texture Advection: Preserving Both Spectrum and Velocity Field.
//IEEE Transactions on Visualization and Computer Graphics 17, 11 (2011), 1612â€“1623
vec3 PreserveVariance(vec3 linear_col, vec3 mean_col, float moment2)
{
    return (linear_col - mean_col) / sqrt(moment2) + mean_col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 pixel_coord = ivec2(fragCoord.xy);
    fragColor = texelFetch(iChannel0, pixel_coord, 0);
    if(pixel_coord == ivec2(0, 0))
    {
        fragColor.r = max(fragColor.r, iMouse.z);
        fragColor.g = clamp(fragColor.g + (iMouse.z > 0.5f ? iTimeDelta : -iTimeDelta) * 4.0f, 0.0f, 1.0f);
    }
}
