vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    float scaling = 2.0;
    vec2 olduv = uv;
    uv *= scaling;
    uv.x *= iResolution.x / iResolution.y;

    // Output to screen 
    uv = exp(-abs(uv*1.1)+max(min(iTime-2.0,40.0),0.0))*max(min(iTime-5.4,0.0),-2.0)  + min(max(iTime - 5.4,0.0),25.0)*exp(abs(uv))*length(uv);
    vec3 color1 = palette(abs(1.0-(sin(uv.x)))/scaling);
    vec3 color2 = palette(sin(uv.y)/scaling);

    uv = 0.2*uv + 0.8*exp(-uv); //optional
    float weight = floor(uv.x*uv.y/pow(scaling,2.0))*pow(abs(cos(iTime+1.57)),2.0);
    vec3 color3 = palette(weight);

    vec3 color = weight*color1 + (1.0-weight)*color2 + color3/2.0;
    color += texture(iChannel0,olduv*scaling).rgb*0.5;

    //vec3 color = palette(value*exp(-length(uv)/scaling) + iTime*0.5);
    float intensity = abs(cos(length(uv)*100000.0));

    fragColor = vec4(color,1.0);
    fragColor = -exp(-tan(length(uv))*fragColor*max(min(iTime-5.3,0.0),-2.0))*min(iTime-5.3,0.0) + min(max(iTime - 5.4,0.0),1.0)*fragColor;
    fragColor = vec4(fragColor.xyz*intensity,1.0);
}
