// CC0: Year 2023 distance field
//  Wanted to have a distance field for 2023

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define SCA(a)      vec2(sin(a), cos(a))
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

const float lw = 0.1;

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// License: CC0, author: Mårten Rånge, found: https://github.com/mrange/glsl-snippets
float pabs(float a, float k) {
  return -pmin(a, -a, k);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float horseshoe(vec2 p, vec2 c, float r, vec2 w) {
  p.x = abs(p.x);
  float l = length(p);
  p = mat2(-c.x, c.y, c.y, c.x)*p;
  p = vec2((p.y>0.0 || p.x>0.0)?p.x:l*sign(-c.x),
           (p.x>0.0)?p.y:l );
  p = vec2(p.x,abs(p.y-r))-w;
  return length(max(p,0.0)) + min(0.0,max(p.x,p.y));
}

float segmenty(vec2 p, float h, float w) {
  float hh = 0.5*h;
  p.y = abs(p.y);
  p.y -= hh;
  float d0 = length(p);
  float d1 = abs(p.x);
  float d = p.y > 0.0 ? d0 : d1;
  d -= w;
  return d; 
}

float digit_0(vec2 p) {
  vec2 p0 = p;
  p0.y = -abs(p0.y);
  p0.y -= -0.3;
  float d0 = horseshoe(p0, SCA(0.*PI), 0.5, vec2(1.0, lw));

  float d = d0;
  return d;
}

float digit_2(vec2 p) {
  const mat2 r0 = ROT(radians(132.5));
  vec2 p0 = p;
  vec2 p2 = p;
  p2.y -= -0.8;
  p2 = p2.yx;
  p0.y -= 0.3;
  p0 *= r0;
  vec2 p1 = p0;
  p1.x -= -0.5;
  p1.y -= 0.25;
  float d0 = horseshoe(p0, SCA(0.*PI), 0.5, vec2(0.1, 0.0))-lw;
  float d1 = segmenty(p1, 1.655, lw);
  float d2 = segmenty(p2, 1.0-lw, lw);

  float d = d0;
  if (p0.y > 0.0 && p0.x < 0.0) {
    d = d1;
  }
  d = min(d, d2);
  return d;
}


float digit_3(vec2 p) {
  const mat2 r0 = ROT(radians(132.));
  vec2 p0 = p;
  p0.y = pabs(p0.y, 0.1);
  p0.y -= 0.35;
  p0 *= r0;
  float d0 = horseshoe(p0, SCA(0.*PI), 0.45, vec2(0.15, 0.0))-lw;
  float d = d0;
  return d;
}

float year2023(vec2 p) {
  p.x -= -0.575;
  vec2 p0 = p;
  p0.x -= 0.06;
  vec2 p1 = p;
  float sx = sign(p1.x);
  p1.x = -abs(p1.x);
  p1.x -= -1.2;
  p1.x *= -sx;
  vec2 p2 = p;
  p2.x -= 2.35;
  float d0 = digit_0(p0);
  float d1 = digit_2(p1);
  float d2 = digit_3(p2);
  float d = d0;
  d = min(d, d1);
  d = min(d, d2);
  return d;
}

float df(vec2 p) {
  float zz = 0.5;
  p /= zz;
  float d = year2023(p);
  return d*zz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  float d = df(p);
  float aa = 2.0/RESOLUTION.y;

  vec3 col = vec3(0.0);
  col = mix(col, vec3(1.0), smoothstep(aa, -aa, d));
  col = sqrt(col);

  fragColor = vec4(col, 1.0);
}
