mat2 rot(float a) {
	return mat2(cos(a),sin(a),-sin(a),cos(a));	
}

const vec3 l = vec3(1.);
const vec3 sundir = normalize( vec3(-1.,.7,-1.) );
const vec3 suncol = vec3(1.,.8,.5);

float cloud(in vec3 p) {
	float s=.5,e=(2.7-p.y)*(5.-p.y)+5.;
    p.xz *=.7;
    for(int i=0;i<7;i++)
            p.xz*=rot(1.),
            e-=abs(dot(sin(p*s+2.*cos(1.7*p.zxy*s)),l*.5))/s,
            s*=1.7;
    return .6*e;
}


float ground(in vec3 p) {

	float s=2.,f;	
	for(f=p.y;s<5e2;s*=1.7)
            p.xz*=rot(1.),            
            f+=dot(sin(p.xz*s*.4+2.*cos(.7*p.zx*s*.4))/s,l.xz);    
    return f*.52;
}

vec3 sky( in vec3 ro, in vec3 rd ,in vec2 res)//modified from IQ clouds
{

    // background sky         
    float sun = max( dot(sundir,rd), 0.0);    
    vec3 col = vec3(0.6,0.6,0.78) - abs(rd.y)*0.5*vec3(1.0,0.4,.05) ;    
    col += 0.4*suncol*pow( sun, 8.0 ); 
    // clouds        
    float k = res.x, c = res.y;
    if(c>.0)
       col *= 1.-.4*c,
       col += 3.*(.5+k)*c*suncol,
       col += vec3(0.2,0.08,0.04)*pow( sun, 3.0 )*c;    
    return col;
}


vec3 raycast( in vec3 ro, vec3 rd )
{
    float t = 4.,e,f,de=0.,df,d,c=1.,dt=.2,r=1.;
    vec3 col= vec3(0.),p,skycol;
    for( int i=0; i<100; i++ )
	{                
        p = ro+t*rd;
        if(p.y<0.)rd.y = -rd.y, ro = p, t = .2, r=.5;
        e = cloud(p), f=ground(p),             
        d =min(min(f,p.y+.1),max(e,.07));
        t +=.7*d;
        if(e<.001)de+=max(cloud(p + dt*sundir)-e,0.)/dt;
        c *= 1./(1.+ exp(-16.*e));        	
    }
    skycol = r*sky(ro,rd,vec2(.04*de,(1.-c)*.15)); 
    if(f>.5)return skycol;
    df=max(ground(p+.02*sundir)-f,.0)*50.+.6;
    f=.55-exp(-f*f);
    col = vec3(f, f*f,f*f*f)*df;
    float maxd = 50.;
    col = mix(col, skycol, smoothstep(.2, .99, min(t, maxd)/maxd));
    return r*col;
}


void mainImage( out vec4 fragColor, in vec2 u )
{
	float t = iTime;
     vec2  R = iResolution.xy,
           q = ( u+u - R ) / R.y;   

    // camera

    vec3 ro = vec3(0.), rd = normalize(vec3(q,3.) );
    rd.yz*=rot(-.15); 
    rd.xz*=rot(0.1*t);
    ro.x -=t*.4;
    ro.y += 2.;

	// raymarch 

    vec3 col = raycast(ro,rd);

	// shade

    col = log(1.+col);
    col = clamp(col,0.,1.);
    fragColor = vec4( col, 1.0 );

}
