    // camera angle
    vec3 ca = vec3(
        PI/2.+m.y*PI/1.5,
        PI/2.-m.x*PI/1.,
        0
    );

    float camz = PI;
    vec3 camera = vec3(0,0,camz)*rotX(ca.x)*rotY(ca.y);
    vec3 plane = vec3(uv,1)*rotX(ca.x)*rotY(ca.y);

    Ray r = initRay(camera,plane);

    //l[0].p = vec3(2,2,2);//*rotX(ca.x)*rotY(ca.y);
    l[0].p = vec3(-2,2,2)*rotX(ca.x)*rotY(ca.y);

    march(r,0);
