#define time iTime
#define resolution iResolution
#define mouse iMouse.xy
#define PI 3.14159265
#define MAX_DIST  100.
#define MIN_DIST  .001
#define MAX_STEP  256
#define MAX_REF   2

#define rotX(a) mat3(      1, 0,       0, 0, sin(a), cos(a),      0,- cos(a), sin(a))
#define rotY(a) mat3( sin(a), 0, -cos(a), 0,      1,      0, cos(a),       0, sin(a))
#define rotZ(a) mat3( sin(a), 0, -cos(a), 0,      1,      0, cos(a),       0, sin(a))
#define rot2D(a) mat2(sin(a),cos(a),-cos(a),sin(a))

const int LIGHTS = 1;

struct Ray {
	vec3 o;
	vec3 p;
	vec3 dir;
	vec3 c;
	float dist;
    float rl;
};

struct Light {
	vec3 p;
	vec3 c;
    vec3 L;
},l[LIGHTS];

vec3 N = vec3(0);
vec3 V = vec3(0);

float polygon(vec2 uv,vec2 p,float r,float n){
    uv = uv - p;
    n = floor(n);
    float pa = PI-atan(uv.x,uv.y);      // position angle
    float sa = 2.*PI/n;                 // side angle
    float id = (pa - mod(pa,sa)) / sa ;
    float ra = id * (sa) + sa/2.;       // rotate angle
    float ir = cos(sa/2.)*r;            // inner radius
    float sl = sin(sa/2.)*r;            // half side length

    uv = rot2D(ra)*uv;
    uv += vec2(-ir,0);

    if(uv.x < 0.)return uv.x;

    return length(vec2(uv.x,uv.y - clamp(uv.y,-sl,sl)));
}

float dist(vec3 p){
	float d = 0.;

    float poly1 = polygon(p.xz,vec2(-.5,0),.7,3.)-.3;
    float poly2 = polygon(vec2(poly1,p.y),vec2(0,0),.1,5.)-.01;

    d = poly2;

    poly1 = polygon(p.xy,vec2(.5,0),.7,5.)-.3;
    poly2 = polygon(rot2D(2.*PI/3.)*vec2(poly1,p.z),vec2(0),.1,6.)-.01;

    d = min(poly2,d);
	return d;
}

float lightDist(vec3 p,float d){
    for(int i = 0;i<LIGHTS;i++)
        d = min(d,length(p - l[i].p)-.1);
	return d;
}

void march(inout Ray r,int t){
	for(int i = 0;i<MAX_STEP;i++){
		r.p = r.o + r.dist*r.dir;
		float d = dist(r.p);
		d = t == 0 ? d : min(d,lightDist(r.p,d));
		if(d <= MIN_DIST || d > MAX_DIST)break;
		r.dist += d;
	}
    r.rl += r.dist;
}

vec3 sNormal(vec3 p){
	vec2 t = vec2(.001,0);
	float d = dist(p);

	return normalize(d - vec3(
		dist(p-t.xyy),
		dist(p-t.yxy),
		dist(p-t.yyx)
	));
}

float diffuse(vec3 p,vec3 L){
	return max(dot(sNormal(p),normalize(L)),0.);
}

void lNormal(inout Light l,Ray r){
	l.L = normalize(l.p-r.p);
}

vec3 render(Ray r){
    vec3 c = vec3(0);
    N = sNormal(r.p);
    for( int i = 0;i< LIGHTS;i++){
        lNormal(l[i],r);
        c += diffuse(r.p,l[i].L);
    }
    c *= .5+.5*N;

    if(r.dist > MAX_DIST)c = vec3(.1);
    return c;
}

Ray initRay(vec3 c,vec3 p){
    // c camera
    // p projection plane
    Ray r;
    r.o = c;
    r.p = r.o;
    r.dir = normalize(p-c);
    r.c = vec3(0);
    r.dist = 0.;
    r.rl = 0.;
    return r;
}
