vec3 palette(float t) {
    vec3 a = vec3(0.5, 0.4, 0.6);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 0.9, 1.0);
    vec3 d = vec3(0.263, 0.416,0.557);



    return a + b*cos( 6.28318*(c*t+d) );
}


float sdfCircle(vec2 uv) {
    return length(uv) - 0.5;
}

float sdfHexagon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

float sdfHexagram( in vec2 p, in float r )
{
    const vec4 k = vec4(-0.5,0.8660254038,0.5773502692,1.7320508076);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= 2.0*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);
    return length(p)*sign(p.y);
}

float sdf(vec2 uv) {
    return sdfHexagram(uv, 1.0);
}

float beat(float t) {
    return t + ((sin(t * 8.0) + sin(t * 6.0) + sin(t * 18.0)) / 2.0) * ((sin(t * 0.01) + 1.0) / 2.0);
}

const float PHI = (1.0 + sqrt(5.0)) / 2.0;

vec2 rotate(vec2 u, float theta) {
    float c = cos(theta);
    float s = sin(theta);
    mat2 R = mat2(c, s, -s, c);

    return R * u;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    uv = rotate(uv, iTime / 7.0);
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);
    const float PI = 3.14159265;
    vec3 modTime = vec3(sin(sdf(uv) - iTime), sin(sdf(uv) - iTime - 2.0 * PI / 3.0), sin(sdf(uv) - iTime - 4.0 * PI / 3.0)) - iTime;
    const float iterations = 3.0;

    float beatTime = (sin(length(uv) * 6.0 - beat(iTime)));

    for (float i = 0.0; i < iterations; ++i)
    {
        uv = fract(uv * PHI) - 0.5;

        float d = (sdfHexagram(uv, i) * exp(-sdfHexagon(uv0, sin(iTime))));
        vec3 col = palette(iTime*0.4 + sdf(uv0) + sdf(uv) + i*0.4);

        d = abs(sin(d * 8.0 + beatTime*0.6) / 8.0);
        d = pow(0.01 / d, 1.2);



        finalColor += col * d / iterations;
    }


    fragColor = vec4(finalColor, 1.0);
}

