//https://iquilezles.org/articles/distfunctions2d
///
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(0.8),b,d)
#define S2(d,b) smoothstep(6.0*antialiasing(0.5),b,d)
#define PI     3.14159265
#define TWO_PI 6.28318530

#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}    

float intersectSDF(float distA, float distB)
	{ return max(distA, distB);}
float unionSDF(float distA, float distB)
	{ return min(distA, distB);}
float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}


vec3 DrawFigBordeR(vec3 pColObj, vec3 colOut, float distObj, float r )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-r,0.0));
  return colOut;
}

vec3 DrawFigBorde(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.005,0.0));
  return colOut;
}

vec3 DrawFigBordeB(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.05,0.0));
  return colOut;
}

float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);  }

float sdCircle( vec2 p, float r )
{ return length(p) - r;}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{  vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec3 mozaico(vec2 p, vec3 colOut)
{   float escala=0.5;
    float d=-999.9;
    d = mod(floor(p.x*escala)+floor(p.y*escala*2.0),2.0);
    vec3 col=  mix( vec3(1.0), vec3(1.0,0.0,.0), d ) ;
    colOut= col;    
    return colOut;
}

vec3 monta(vec2 p, vec3 colOut,vec3 colIn)
{   float d1=cos(p.x*0.25 +cos(p.x*0.5))+p.y-6.2;
    float d2=p.y-4.8;
    d1= differenceSDF(d1,d2);
    colOut= DrawFigBordeB(colIn,colOut,d1);
    return colOut;
}

vec3 mozaicoFull(vec2 p, vec3 colOut)
{   float d1=p.y-4.8;
    float d2=p.y-2.8;
    d1= differenceSDF(d1,d2);
    vec3 colmoz= mozaico(p-vec2(0.0,1.8), colOut);
    colOut= DrawFigBorde(colmoz,colOut,d1);
    return colOut;
}


vec3 Lineasx(vec2 p , vec3 colOut)
{  vec2 a, b,b2,b3,b4;
    mat2 mm=Rot(-iTime*2.0);    
    a=vec2(0.0,0.0)*mm;
    b=vec2( 0.5,0.20);
    b2=vec2(-0.5,-0.20);
    b3=vec2( 0.5,0.20)*Rot(radians(90.0));
    b4=vec2(0.5,0.20)*Rot(radians(-90.0));

    float d2a= sdSegment( (p-vec2(1.0,-0.25))*mm,a,b );
    float d2b= sdSegment( (p-vec2(1.0,-0.25))*mm,a,b2 );
    float d2c= sdSegment( (p-vec2(1.0,-0.25))*mm,a,b3 );
    float d2d= sdSegment( (p-vec2(1.0,-0.25))*mm,a,b4 );

    colOut= DrawFigBordeB(vec3(0.0),colOut,d2a);
    colOut= DrawFigBordeB(vec3(0.0),colOut,d2b);
    colOut= DrawFigBordeB(vec3(0.0),colOut,d2c);
    colOut= DrawFigBordeB(vec3(0.0),colOut,d2d);
    return colOut;
}

vec3 vehiculo(vec2 p, vec3 colOut)
{  p.x=opRep1D(p.x,10.0);
    p.y+=sin(iTime);
    vec2 p0=p;
    float d0a= sdBox(p-vec2(0.0,1.0), vec2(0.5,0.5) );
    float d0= sdBox(p, vec2(1.7,0.5) );

    p.y+=0.25;
    p.x=abs(p.x)-1.0;
    float d1= sdCircle(p,0.5 );
    colOut= DrawFigBorde(vec3(1.,0.5,0.0),colOut,d0a);
    colOut= DrawFigBorde(vec3(1.,0.0,0.0),colOut,d0);
    colOut= DrawFigBorde(vec3(1.0,1.0,0.0),colOut,d1);
    p=p0;
    colOut= Lineasx(p , colOut);
    colOut= Lineasx(p-vec2(-2.0,0.0) , colOut);
    return colOut;
}


vec3 nubes(vec2 p, vec3 colOut)
{    
    vec2 centro=vec2(0.0,0.0);
    float c1= sdCircle(p-vec2(0.01,0.05), 0.105);
    float c2= sdCircle(p-vec2(0.15,0.02), 0.065);
    float c3= sdCircle(p-vec2(-0.12,0.02), 0.065);
    float b1=sdBox(p-vec2(0.0,-0.04), vec2(0.3,0.05 ) );

    float res=unionSDF(c1, c2);
    res=unionSDF(res, c3);
    res=differenceSDF(res,b1 );
    colOut=DrawFigBordeR(vec3(1.0), colOut, res,0.001 );    
	return colOut;    
}
vec3 pista(vec2 p, vec3 colIn, vec3 colOut)
{  float t=iTime;
   //hacer pasto
   float d1=p.y-2.8;
   float d2=p.y+3.8;
   d1= differenceSDF(d1,d2);

   colOut= DrawFigBorde(colIn,colOut,d1);
    colOut= mozaicoFull(p-vec2(-t*0.5,0.0), colOut);
    colOut= monta(p,colOut,vec3(0.2,0.8,0.2));
    colOut= vehiculo(p+vec2(t*3.0,0.0),colOut);
    return colOut;
}

vec3 render_sky_color(vec2 uv)
{   vec2 uv0=uv;
    uv.y+=-0.2+0.5*sin(iTime);
    float t = (uv.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
    vec3  sky = mix(vec3(.0, .1, .4)*col, vec3(.3, .6, .8), 1.0 - uv.y);
    uv=uv0;

    sky= nubes(uv-vec2(0.0,1.0), sky);
    sky= nubes(uv*1.5-vec2(2.0,2.0), sky);
    uv.x=mod(uv.x+iTime*0.5,2.0);
    sky= nubes(uv*1.25-vec2(2.0,1.5), sky);

	return sky;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
    uv-=vec2(0.0,-0.5);
    float t=iTime;
    vec3 col=vec3(1.0);
    col= render_sky_color(uv);

    float esc=8.0;
    uv*=esc;
    vec2 uv0=uv;

    col=pista(uv+vec2(t,0.0),vec3(0.55), col);

    col=pow(col,  vec3(0.554545));
    fragColor = vec4(col,1.0);
}
