/*
This is a demo that shows how to disrupt texture tiling, sample different 
biomes, and blend them seamlessly. This technique is a slight variation of 
the Directional Flow method.

This code is extensively commented to help you understand its inner workings. 
It's important to note that it's designed to work with a range of features 
Shadertoy does not have, such as Texture Arrays and Gather functions. If 
you have access to these native functions, use them instead. The 
Directional Flow algorithm is a versatile tool that can create a variety 
of effects, which go beyond the scope of this shader.

This shader uses dithering to switch between samples and is so able to use a
single sample to achive similar results to bilinear interpolation. If you 
use TemporalAA to blur the result it looks pretty much identical to a proper
bilinear interpolation while being ~4x faster.

This demo includes mouse controls to adjust the light direction, zoom and 
various parameters mentioned below this header.

Additional reading material:

CatlikeCoding's has a well explained guide on Directional Flow:
https://catlikecoding.com/unity/tutorials/flow/directional-flow/

COD recently made a presentation outlining a similar method:
https://advances.realtimerendering.com/s2023/index.html#CODTerrain

Here's my 4-sample Bilinear Biom Texture Shuffle:
https://www.shadertoy.com/view/dsVyRw

See here how to Dither Bilinear Weights:
https://www.shadertoy.com/view/ddGcWh
*/

// The number of previous samples looked at to blur result, with a similar effect to
// Temporal Anti-Aliasing (TAA) which you should use instead if you can.
#define TEMPORALBLUR 8 

// Enable debug grid where the two mappings blend with each other
#define LINETHICKNESS 0.01
//#define SHOWGRID

// Size of interpolation grid
#define GRIDSIZE 32.0

// Power scale: 1 means no contrast, 50 means maximum contrast
#define CONTRAST 2.0

// Maximum noise offset in grid scale
#define NOISESTRENGTH 1.2

// Scale of noise in grid scale
#define NOISESCALE 1.3 

// Size of textures in grid scale
#define DETAILSCALE 1.0

// Function to select a texture using an index. Acts like a crude texture array.
vec4 textureArray(int index, vec2 uv, vec2 duvdx, vec2 duvdy) {
    vec4 color;    
    if (index == 0) {
        // the gravel texture is just greyscale so we make it pretty with a colormap
        color = vec4(bone(textureGrad(iChannel0, uv, duvdx, duvdy).x),0);
    } else if (index == 1) {
        color = textureGrad(iChannel1, uv, duvdx, duvdy);
    } else if (index == 2) {
        color = textureGrad(iChannel2, uv, duvdx, duvdy);
    } else {
        color = textureGrad(iChannel3, uv, duvdx, duvdy);
    }
    return color;
}

// Function for generating normals using 3 texture samples.
// Original source: https://www.shadertoy.com/view/3sSSW1
vec3 HeightToNormal(int heightMap, vec2 uv, vec2 duvdx, vec2 duvdy, float strength) {
    vec2 s = 1.0 / iChannelResolution[0].xy;

    float textureOffset = 1.0;

    float p = textureArray(heightMap, uv, duvdx, duvdy).y;
    float h1 = textureArray(heightMap, uv + s * vec2(textureOffset,0), duvdx, duvdy).y;
    float v1 = textureArray(heightMap, uv + s * vec2(0,textureOffset), duvdx, duvdy).y;

   	vec2 xy = (p - vec2(h1, v1));

    return normalize(vec3(xy * strength, 1.0));
}

// Struct to hold material values
struct materialVal {
    vec3 color;
    vec3 normal;
};

// Generate biomes blending with texture tiling breakup in one
materialVal biomTexShuffle(sampler2D biomTex, vec2 uv, vec2 fragCoord, float time,
    float gridSize, float contrast, float noiseStrength, float rndScale, float texScale) {

    // Tiling Noise ________________________________________________________
    // The tiling noise idea is from COD:
    // https://advances.realtimerendering.com/s2023/index.html#CODTerrain

    // Scale noise coordinates to be independent of biome dimension for convenience
    vec2 noiseUV = uv * gridSize / rndScale;

    // Sample noise vector; using a 2D texture of Perlin noise would be better
    vec2 noiseVec = simplexNoiseVec(noiseUV);

    // Apply strength and correct for biome dimension again
    noiseVec = noiseVec * noiseStrength / gridSize;

    // Distort the grid UVs to hide the tiling pattern
    vec2 gridUV = uv - noiseVec;

    // Weights ____________________________________________________________

    // Generate zigzag pattern
    vec2 zigZag = abs(fract(gridUV * gridSize/2.0 - 0.25)*2.0-1.0);

    // Invert zigzag as a partial of the weights
    vec2 infZigZag = 1.0 - zigZag;

    // Generate alpha values (weights) for bilinear interpolation.
    float weightA = zigZag.x * zigZag.y;
    float weightB = zigZag.x * infZigZag.y;
    float weightC = infZigZag.x * zigZag.y;
    float weightD = infZigZag.x * infZigZag.y;

    // Combine weight/alpha values. 
    vec4 weights = vec4(weightA, weightB, weightC, weightD);

    // Change the weights contrast for less blurry results.
    weights = smoothContrast(weights, contrast);

    // Dither _____________________________________________________________

    // Sample dither noise
    float dither = ScreenSpaceDither12(fragCoord, time);

    // Smooth contrast creates artifacts in the dither. Clamping removes them
    dither = clamp(dither, 0.01, 0.99);

    // Applies float dither to weight values.
    // The weights are grouped cumulatively, and if the dither value is within one 
    // cumulative section for one of the weights, that weight is selected
    vec2 offset;
    if (weights.x > dither) {
        // Weight A
        offset = vec2(0.0, 0.0);
    } else if (weights.x + weights.y > dither) {
        // Weight B
        offset = vec2(0.0, 0.5);
    } else if (1. - weights.w > dither) {
        // Weight C
        // because sum of all weights is 1, so 1-w = sum of xyz
        offset = vec2(0.5, 0.0);
    } else {
        // Weight D
        offset = vec2(0.5, 0.5);
    }

    // Biom ________________________________________________________________

    // Coordinates of every other grid point
    vec2 scaledCoords = (floor(gridUV * (gridSize/2.) +.25 +offset) -offset)*2.;

    // Get biome texture resolution
    vec2 texSize = vec2(textureSize(biomTex, 0));

    // Modulo for looping biome textures
    ivec2 texelCoords = ivec2(fract(scaledCoords/gridSize) * texSize);

    // Sample the texels.
    float biom = texelFetch(biomTex, texelCoords, 0).x;

    // Increase to separate numbers
    float biomCount = 4.; // Since we can only have 4 textures

    // Scale biom sample to seperate integers
    //int biomID = int(biom * biomCount);
    // Since I don't have an actual biom texture I'll do some modifications 
    int biomID = int(biom * 7.);

    // Sample Detail Textures ______________________________________________

    // This hash doesn't like 0. Mod is optional and makes it repeat at uv 0-1.
    vec2 hashCoords = mod(scaledCoords, gridSize) + 1.; 

    // Obtain a cheap random vector 
    vec2 rnd = hash22(hashCoords) * 2.0 - 1.0;

    // Scale UVs for detail textures, should be higher than texSize
    vec2 detailUV = uv * gridSize * texScale;

    // Calculate mip as the automatic ones don't work for dithering
    vec2 duvdx = dFdx(detailUV);
    vec2 duvdy = dFdy(detailUV);

    // Normalize vector to prevent rotation from scaling the result
    vec2 rotation = normalize(rnd);

    // Randomly rotate
    detailUV = rotateUV(detailUV, rotation);

    // Randomly offset
    detailUV += rnd;

    // Sample detail texture
    vec3 color = textureArray(biomID, detailUV, duvdx, duvdy).xyz; 

    // Sample Normal, if you can use an actual normal texture instead
    vec3 normal = HeightToNormal(biomID, detailUV, duvdx, duvdy, 50.0);

    // Since we rotated the UVs we have to rotate the normals too
    normal = vec3(rotateUV(normal.xy, rotation), normal.z);

    #ifdef SHOWGRID
        // overlaying the edges of the alpha mask
        vec4 mask = step(abs(weights - 0.1), vec4(LINETHICKNESS));
        color = max(mask.xyz, color * (1.-mask.w));
    #endif

    // Combine color and normal into a struct
    return materialVal(color, normal);;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Square UVs, with 0.5 in the center
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.x + 0.5; 

    // If the mouse was not clicked, animate light
    vec3 lightDir;
    float zoom;
    if (iMouse.x > 0.0) {
        // scale mouse coords to 0-1 range of screen
        vec2 mouse = iMouse.xy / iResolution.xy;

        // Mouse controls zoom with steps
        float zoomsteps = 10.;
        zoom = mix(1.0, 0.10, pow(floor(mouse.y*zoomsteps)/zoomsteps,.5));

        // Mouse controls light direction
        float angle = mouse.x * 3.14 * 3.;
        lightDir = normalize(vec3(cos(angle), sin(angle), 1.0));
    } else {
        // Animated zoom
        float zoomTime = iTime * 0.1;
        zoom = mix(1.0, 0.10, cos(zoomTime) * 0.5 + 0.5);

        // Time controls light direction
        float lightTime = iTime * 0.3;
        lightDir = normalize(vec3(cos(lightTime), sin(lightTime), 1.0));
    } 

    // Apply zoom on the center
    uv = (uv - 0.5) * zoom + 0.5;

    // Initialize color and normal
    vec3 color = vec3(0.0);
    vec3 normal = vec3(0.0);

    #if TEMPORALBLUR <= 1
        // We just take a single sample from the ditheredSample function.
        materialVal terrain = biomTexShuffle(iChannel3, uv, fragCoord, iTime,
            GRIDSIZE, CONTRAST, NOISESTRENGTH, NOISESCALE, DETAILSCALE);
        color = terrain.color;
        normal = terrain.normal;

    #else
        // We take multiple samples from the ditheredSample function and average them.
        // This is normally done automatically by your TAA or motion blur
        for(int i = 0; i < TEMPORALBLUR; i++) {
            materialVal terrain = biomTexShuffle(iChannel3, uv, fragCoord, iTime - float(i),
                GRIDSIZE, CONTRAST, NOISESTRENGTH, NOISESCALE, DETAILSCALE);
            color += terrain.color;
            normal += terrain.normal;
        }
        color /= float(TEMPORALBLUR);

        // technically wrong but looks the same as Slerp
        normal = normalize(normal / float(TEMPORALBLUR)); 
    #endif

    #ifndef SHOWGRID           
        // Apply lighting to show the normals too
        color = phongLighting(color, normal, vec3(0,0,1), lightDir);
    #endif

    fragColor = vec4(color, 1.0);
}

//_____________________GENERIC FUNCTIONS______________________________

// My customized hash which is faster than a texture lookup
// inspired by David Hoskins https://www.shadertoy.com/view/XdGfRR
#define VPRIME1 uvec2(3480082861U, 2420690917U)
#define VPRIME2 uvec2(1317666547U, 2149110343U)
#define SMALLESTFLOAT (1.0 / float(0xffffffffU))
vec2 hash22(vec2 p)
{
	uvec2 q = uvec2(ivec2(p)) * VPRIME1;
	q = (q.x & q.y) * VPRIME2;
	return vec2(q) * SMALLESTFLOAT;
}

// rotates UV by direction
vec2 rotateUV(vec2 uv, vec2 direction){
    // Calculate the rotation matrix
    mat2 rotationMatrix = mat2(
        -direction.x, direction.y,
        direction.y, direction.x
    );

    // Rotate the UV coordinates
    return rotationMatrix * uv;
}

// valve edition (slightly modified to be a true 12 dither)
// http://alex.vlachos.com/graphics/Alex_Vlachos_Advanced_VR_Rendering_GDC2015.pdf
// input in pixels (ie not normalized uv)
float ScreenSpaceDither12(vec2 vScreenPos, float time) {
    float vDither = dot( vec2( 171.0, 231.0 ), vScreenPos.xy + time); 
    return fract( vDither / 103.0);
}

// Increases the steepness of Alpha while preserving 0-1 range and 1 sum
// See 2 value example https://www.desmos.com/calculator/n6gpory8yq
vec4 smoothContrast(vec4 alpha, float contrast) {
    // increase steepness using power
    vec4 powAlpha = pow(alpha, vec4(contrast));

    // normalize back to precentage of 1
    return powAlpha/(powAlpha.x + powAlpha.y + powAlpha.z + powAlpha.w);
}

// makes bone colormap with polynimal 6
vec3 bone(float t) {
    const vec3 c0 = vec3(-0.005007,-0.003054,0.004092);
    const vec3 c1 = vec3(1.098251,0.964561,0.971829);
    const vec3 c2 = vec3(-2.688698,-0.537516,2.444353);
    const vec3 c3 = vec3(12.667310,-0.657473,-8.158684);
    const vec3 c4 = vec3(-27.183124,8.398806,10.182004);
    const vec3 c5 = vec3(26.505377,-12.576925,-5.329155);
    const vec3 c6 = vec3(-9.395265,5.416416,0.883918);
    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
}

// Phong lighting model
vec3 phongLighting(vec3 albedo, vec3 normal, vec3 cameraDir, vec3 lightDir) {
    // Ambient
    float ambientStrength = 0.25;
    vec3 ambient = ambientStrength * albedo;

    // Diffuse 
    float diff = clamp(dot(normal, lightDir), 0.5, 1.0);
    vec3 diffuse = diff * albedo;

    // Specular
    float specularStrength = 0.25;
    vec3 viewDir = normalize(cameraDir);
    vec3 reflectDir = reflect(-lightDir, normal);  
    float specular = pow(clamp(dot(viewDir, reflectDir), 0.0, 1.0), 36.0);
    specular *= specularStrength;  

    // Combine results
    return ambient + diffuse + specular;
}

//_____________________________Simplex Noise______________________________
// Copyright Â© 2013 Inigo Quilez
// https://www.shadertoy.com/view/Msf3WH

vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

// 4 layer simplex noise range -1 to 1
float simplexNoise(vec2 uv) {    
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    float f;
    f  = 0.5000*noise( uv ); uv = m*uv;
    f += 0.2500*noise( uv ); uv = m*uv;
    f += 0.1250*noise( uv ); uv = m*uv;
    f += 0.0625*noise( uv ); uv = m*uv;
    return f;
}

// generates 2 vector of simplex noise
vec2 simplexNoiseVec(vec2 uv) {
    // sample noise function
    float noiseX = simplexNoise(uv);    
    float noiseY = simplexNoise(uv + 691.); // with prime offset

    // make noise vector
    return vec2(noiseX, noiseY);
}
