void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 a_n_mouse_coords_normalized = iMouse.xy / iResolution.xy;
    vec2 a_n_frag_coords_normalized = fragCoord.xy / iResolution.xy;


    float n_lines_glob = 500.0; 
    float n_lines = n_lines_glob; 
    float n_mod = iResolution.x / n_lines;
    float n_brightness = 1.0;

    if(
        a_n_frag_coords_normalized.y > 0.0
        &&
        a_n_frag_coords_normalized.y < 0.75
    ){

        if(mod(fragCoord.x, n_mod) > (n_mod /2.0)){
            n_brightness = 1.0;
        }else{
            n_brightness = 0.0;
        }
    }
    if(
        a_n_frag_coords_normalized.y > 0.25 
    ){
        float n_lines2 = n_lines+1.0;
        float n_mod2 = iResolution.x / n_lines2;
        float n_trans = n_mod * (a_n_mouse_coords_normalized.x*(n_lines2/20.0));
        if(
            mod(fragCoord.x + n_trans, n_mod2) 
            > (n_mod /2.0)
          ){
                n_brightness = 0.0;
           }
    }


    fragColor = vec4(n_brightness);


}
