/* "Digital Abyss" by @kishimisu (2022) - https://www.shadertoy.com/view/Dsj3WW
 *   
 * Interact with the mouse!  */

#define AA 1 // Antialiasing level

void mainImage(out vec4 O, in vec2 F) { 
_AA_START     
    float a, r, t = 3.;
    vec2  n       = iResolution.xy,
          i       = (F+(o-n)*.5)/n.y,
          m       = iMouse.xy/n*2.-1.;

    for (a = 0.; a < 2e2 && t > .001; a++) {
        vec3 p = r*vec3(i*.35 - m*.05, 1.);
        p.xy  *= mat2(cos(r*m.x*.07 + vec4(0,33,11,0)));
        p.z   += iTime*.6; p = abs(fract(p)-.5);            
        r += t = (p.x + p.y + p.z - .12)*.6;        
    }
    tot += mix(.7 - vec3(r*r*2e-4), 
        1.5*cos(r*(vec3(5.7, 5.76, 5.82) + length(i))), 
        exp(-r*.07) * (1.2-length(i))); 
_AA_END 
}

/* Anti-aliasing code */

#define _AA_START               \
vec3 tot;                       \
for (int j = 0; j < AA; j++)    \
for (int k = 0; k < AA; k++) {  \
vec2 o = vec2(float(j), float(k)) / float(AA) - 0.5;

#define _AA_END       \
} tot /= float(AA*AA);  \
O = vec4(tot, 1.);
