#ifndef PI
#define PI 3.1415926535897932384626433832795
#endif


vec2 rotate2D( in vec2 uv, in float alpha )
{
    float c = cos(alpha);
    float s = sin(alpha);
    uv =  mat2(c, -s, s, c) * (uv - 0.5) + 0.5;
    return uv;
}

vec2 tile(in vec2 uv, in float zoom)
{
    return fract(uv * zoom);
}

float box( in vec2 uv, in vec2 size, in float smoothEdges )
{
  size = 0.5 - size * 0.5;
  vec2 aa = vec2( smoothEdges*0.5 );
  vec2 st = smoothstep( size, size+aa, uv)
          * smoothstep( size, size+aa, 1.0 - uv);    
  return st.x * st.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 invRes = 1.0 / iResolution.xy;
    vec2 uv = (fragCoord.xy - iResolution.xy * .5) / iResolution.yy + 0.5;

    float shiftAngle = PI * 0.25;
    float angle = mod( 1.0 * iTime, PI) + shiftAngle;
    float doShift = step(angle, PI-shiftAngle);
    vec2 shiftOffset = vec2(-0.5, 0.5) / 5.0;

    uv += doShift * shiftOffset;
    uv = tile(uv, 5.0);
    uv = rotate2D(uv, angle);

    float ic = abs(box(uv, vec2(0.71), 0.012) - doShift);
    fragColor = vec4(vec3(0.85*ic , 0.1, 0.55), 1.0);
}
