float _seed;
float rand()
{
    _seed++;
    return hash11(_seed);
}
vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.,-1.);
    float sz = 5.;
    float gyroid = abs(dot(sin(p*sz), cos(p.yzx*sz+iTime))/sz)-.01;
    sz = 18.;
    gyroid = max(gyroid, dot(sin(p*sz), cos(p.yzx*sz+iTime))/sz);
    gyroid = max(gyroid, length(p)-1.);

    gyroid = max(gyroid, -(length(p)-.8));
    acc = _min(acc, vec2(gyroid, 0.));

    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.001, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}
vec3 accCol;
vec3 trace(vec3 ro, vec3 rd, int steps)
{
    accCol = vec3(0.);
    vec3 p = ro;
    for (int i = 0; i < steps && distance(p, ro) < 20.; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.001)
            return vec3(res.x, distance(p, ro), res.y);
        p+=rd*res.x*.5;
        accCol += (normalize(p)*.5+.5)*(1.-sat(res.x/.3))*.02;
    }
    return vec3(-1.);
}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0.);
    float d = 1.5;
    float t = iTime*.3;
    uv *= r2d(sin(t));
    vec3 ro = vec3(sin(t)*d,sin(t)*d*cos(t),cos(t)*d);
    vec3 ta = vec3(0.,0.,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv);
    vec3 res = trace(ro, rd, 128);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = n*.5+.5;
    }
    col += accCol;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    _seed = iTime+texture(iChannel0, uv).x;

    vec3 col = rdr(uv);

    vec2 off = vec2(1., -1.)/(iResolution.x*1.5);

    if (true)// Not so cheap antialiasing
    {
        //col = vec3(1.,0.,0.);
        vec3 acc = col;
        acc += rdr(uv+off.xx);
        acc += rdr(uv+off.xy);
        acc += rdr(uv+off.yy);
        acc += rdr(uv+off.yx);
        col = acc/5.;

    }
    col *= 1.9/(col+1.);
    col = sat(col);
    col = mix(col, texture(iChannel1, fragCoord/iResolution.xy).xyz, .7);
    fragColor = vec4(col,1.0);
}

