#define PI 3.1415926

float checkerboard(vec2 uv, int rows){
    float value = mod(floor(mod(uv.x*float(rows), 2.0)) + floor(mod(uv.y*float(rows), 2.0)), 2.0);
    return value;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec2 bulgePos = iMouse.xy/iResolution.xy * 2.0 - 1.0;
    bulgePos.x *= iResolution.x/iResolution.y;

    vec2 uvMoved = uv - bulgePos;
    float len = length(uvMoved);
    vec2 dir = (uvMoved)/len;
    //uv += dir * (pow(2.0, length(uvMoved))-1.0) * 1.0;
    //uv += dir * pow(smoothstep(0.0, 1.0, length(uvMoved)), 1.0) * 0.5;
    //uv += dir * pow(length(uvMoved), 2.0) * 1.0;

    // Displace the uv coordinates towards the centre of the bulge
    // Generally,the value of this function appears to be the steepness.
    uv -= dir * (0.02 * len)/(pow(len, 6.0)+0.045);

    vec4 col1 = vec4(0.2, 0.80, 0.42, 1);
    vec4 col2 = vec4(0.02, 0.4, 0.12, 1);
    vec3 col = vec3(mix(col1, col2, checkerboard(uv, 6)));
    // Output to screen
    fragColor = vec4(col,1.0);
}
