////////////////////////////////////////////////////////////////////////////////
//
// Quadruple Dragon Curve. mla, 2022.
//
// Four Dragon Curves link up nicely to form a continuous path. Stack based with
// heuristic short cuts.
//
// <mouse>: move
// 'a': show shortcut lines
// 'f': fill
// 'z': variable angle
//
// Uses an explicit stack to keep track of the recursion.
//
////////////////////////////////////////////////////////////////////////////////

const int maxdepth = 10; // How deeply to recurse

// Dragon Curve L-system
#define C(turns,offset) (((turns) << 16) | ((offset) & 0xffff)) // Encoding
const int A = 0, B = 3, END = -1, L = -1, R = 1;
const int program[6] = int[] (C(0,A),C(R,B),C(0,END),C(0,A),C(L,B),C(0,END));
const float factor = sqrt(2.0); // Recursive scale factor

// Global state
vec2 point = vec2(0);         // The drawing point
vec2 movingpoint = vec2(0); // The moving point
bool foundmovingpoint = false;
float pathlen = 0.0;          // Cumulative path length
int windnum = 0;              // Compute winding number here

// Update winding number for segment (q,r)
void polywind(vec2 p, vec2 q, vec2 r) {
  // NR winding number computation
  q -= p; r -= p; // Relative to p
  float t = determinant(mat2(q,r));
  if (q.y <= 0.0) {
    if (r.y > 0.0 && t > 0.0) windnum++;
  } else {
    if (r.y <= 0.0 && t < 0.0) windnum--;
  }
}

// Calculate and draw segment in final level
float drawsegment(vec2 pos, float seglen, float theta, float t) {
  vec2 seg = seglen*vec2(cos(theta),sin(theta));
  if (t >= pathlen && t < pathlen+seglen) {
    foundmovingpoint = true;
    movingpoint = point + (t-pathlen)/seglen*seg;
  }
  vec2 newpoint = point + seg;
  float d = segment(pos,point,newpoint);
  polywind(pos,point,newpoint);
  pathlen += seglen;
  point = newpoint;
  return d;
}

vec2 map(vec2 pos) {
  pos = (2.0*pos - iResolution.xy)/iResolution.y;
  pos *= 1.666;
  return pos;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 pos = map(fragCoord);
  pos -= vec2(-0.8,0.71);
  if (iMouse.z > 0.0) pos -= map(iMouse.xy);
  int stack[maxdepth];
  float totalpath = 4.0*pow(factor,float(maxdepth+1));
  float t = mod(iTime,totalpath); // Moving point position
  float angledelta = 0.0;
  if (key(CHAR_Z)) angledelta = sin(0.1*PI*iTime)*0.05;
  float angle = PI/2.0+angledelta;
  float sfactor = pow(sqrt(1.0+cos(angle)),float(maxdepth));
  pos *= sfactor;
  float magic = 0.5*angle; // Segment rotation at each recursion level
  float d = 1e8, d1 = 1e8; // Line distances
  float theta = -4.0*angledelta;//0.0;       // Cumulative angle
  // Draw four curves, which just happen to join up continuously.
  for (int i = 0; i < 4; i++,theta+=angledelta){
    int depth = 0;
    stack[depth++] = B;
    float seglen = 1.0;
    while (depth > 0) {
      int instr = program[stack[depth-1]++];
      // Decode instruction
      int turns = instr>>16;
      int code = instr<<16>>16;
      theta += float(turns)*angle; // Apply turn
      if (code == END) {
        depth--;    // Terminate level & unwind
        seglen *= factor;
      } else if (depth == maxdepth) {
        d = min(d,drawsegment(pos,seglen,theta,t)); // Segment
      } else {
        // Compute the end point of the segment at the current level
        float depthdelta = float(maxdepth-depth);
        float theta1 = theta + magic*(depthdelta-1.0);
        // Adjust for segment type
        if (code == A) theta1 += magic;
        else theta1 -= magic;
        float seglenfactor = pow(sqrt(1.0+cos(angle)),depthdelta);
        vec2 newpoint = point+seglenfactor*seglen*vec2(cos(theta1),sin(theta1));
        d1 = min(d1,segment(pos,point,newpoint));
        if (distance(pos,0.5*(point+newpoint)) < seglenfactor*seglen) {
          // Do the recursion
          stack[depth++] = code;
          seglen /= factor;
        } else {
          // Modify state as if we had done the full recursion
          polywind(pos,point,newpoint);
          point = newpoint;
          pathlen += seglen*pow(factor,depthdelta);
          // Each non-final segment also changes theta.
          if (code == A) theta += angle;
          else theta -= angle;
        }
      }
    }
  }
  polywind(pos,point,vec2(0)); // Just in case rounding error prevents exact closure...
  float px = fwidth(pos.x);
  vec3 col = vec3(1,1,0.8);
  if (!key(CHAR_F) && (windnum&1) != 0) col.gb *= 0.25;
  if (key(CHAR_A)) col = mix(vec3(0.4), col,smoothstep(0.0,px,d1));
  col = mix(vec3(0), col,smoothstep(0.0,px,d));
  if (foundmovingpoint) {
    col = mix(vec3(0), col,smoothstep(0.0,px,distance(pos,movingpoint)-0.01*sfactor));
  }
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

////////////////////////////////////////////////////////////////////////////////

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

const float PI = 3.1415927;

float segment(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
}

