#define PI   3.1415926535897932384626433832795
#define PI_2 1.5707963267948966192313216916398
#define PI_4 0.78539816339744830961566084581988

// https://www.pcg-random.org/
uint pcg(inout uint state)
{
  uint prev = state * 747796405u + 2891336453u;
  uint word = ((prev >> ((prev >> 28u) + 4u)) ^ prev) * 277803737u;
  state     = prev;
  return (word >> 22u) ^ word;
}
// [0, 1)
float rand(inout uint seed)
{
  uint r = pcg(seed);
  return uintBitsToFloat(0x3f800000u | (r >> 9)) - 1.0f;
}
/**
 * Convert r, g, b to normalized vec3
 */
vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

/**
 * Draw a circle at vec2 `pos` with radius `rad` and
 * color `color`.
 */
vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
    if (d < -rad * 0.5) return vec4(0.0);
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float radius = 0.25 * iResolution.y;

    // Background layer
	vec4 layer1 = vec4(rgb(210.0, 222.0, 228.0), 1.0);
    vec2 uv2 = uv-center;
    float angle = atan(uv2.y, uv2.x) + iTime;
    if (angle > PI) angle -= PI;
    float saw = (angle + PI) * 4.0 / PI;
    saw = saw - float(int(saw));
    saw *= PI_2;
    if (saw > PI_4) saw += PI_2;
    radius += (sin(saw) * 50.0 + sin(iTime * 2.0) * 50.0) * sin(iTime);

	// Circle
	vec3 red = rgb(225.0, 95.0, 60.0);
	vec4 layer2 = circle(uv, center, radius, red);

	// Blend the two
	fragColor = mix(layer1, layer2, layer2.a);

}
